(158, '', 0, '类', NULL, '', '<p>复习：</p>\r\n\r\n<p>---------------------------------------------------</p>\r\n\r\n<p style="margin-left: 40px;">- 类的代码结构</p>\r\n\r\n<p style="margin-left: 40px;">- 构造函数（constructor，重载）</p>\r\n\r\n<p style="margin-left: 40px;">- 原型(链)&nbsp;prototype、__proto__</p>\r\n\r\n<p style="margin-left: 40px;">- 各属性&amp;方法的性质</p>\r\n\r\n<p style="margin-left: 40px;">- 实例 （__prop__？实例有原型吗？）</p>\r\n\r\n<p style="margin-left: 40px;">- 继承，（父类，子类，抽象类）</p>\r\n\r\n<p style="margin-left: 40px;">- (原型属性)属性（配置性，读写性，可枚举性等）</p>\r\n\r\n<p style="margin-left: 40px;">- 命名空间？</p>\r\n\r\n<p style="margin-left: 40px;">- 模块</p>\r\n\r\n<p style="margin-left: 40px;">- 面向对象</p>\r\n\r\n<p style="margin-left: 40px;">- 设计模式（工厂方法等）</p>\r\n\r\n<p>---------------------------------------------------</p>\r\n\r\n<p>__prop__?</p>\r\n\r\n<p>__proto__(IE和Opera不支持此属性查询/设置对象的原型 P139)</p>\r\n\r\n<hr />\r\n<p>&nbsp;</p>\r\n\r\n<p style="line-height:16px;"><span style="background-color:#F0FFF0;">(function($){</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; ------------------------------------</span></p>\r\n\r\n<p style="line-height:16px;"><span style="background-color:#F0FFF0;">&nbsp; &nbsp; //构造函数</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; function AAA(){</span></p>\r\n\r\n<p style="line-height:16px;"><span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; ------------------------------------</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; var prop_1=&quot;</span><span style="color:#FF0000;"><span style="background-color:#F0FFF0;">私有属性</span></span><span style="background-color:#F0FFF0;">&quot;;</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; function fun_1(){</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(&quot;fun_1-[</span><span style="color:#FF0000;"><span style="background-color:#F0FFF0;">私有方法</span></span><span style="background-color:#F0FFF0;">]&quot;);</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; }</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; ------------------------------------</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp;&nbsp; &nbsp;this.prop_2=&quot;</span><span style="color:#FF0000;"><span style="background-color:#F0FFF0;">公有属性</span></span><span style="background-color:#F0FFF0;">&quot;;</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; this.fun_2=function(){</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; return &#39;fun_2-[</span><span style="color:#FF0000;"><span style="background-color:#F0FFF0;">公有方法</span></span><span style="background-color:#F0FFF0;">]&#39;</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; }&nbsp;</span></p>\r\n\r\n<p style="line-height:16px;"><span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; ------------------------------------</span></p>\r\n\r\n<p style="line-height:16px;"><span style="background-color:#F0FFF0;">&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;this.fun_6=function(){</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(&quot;fun_6-[</span><span style="color:#FF0000;"><span style="background-color:#F0FFF0;">特权方法</span></span><span style="background-color:#F0FFF0;">]&quot;);</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; }&nbsp;</span></p>\r\n\r\n<p style="line-height:16px;"><span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; ------------------------------------</span></p>\r\n\r\n<p style="line-height:16px;"><span style="background-color:#F0FFF0;">&nbsp; &nbsp; }</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; ------------------------------------</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; AAA.prop_3=&quot;</span><span style="color:#FF0000;"><span style="background-color:#F0FFF0;">静态属性</span></span><span style="background-color:#F0FFF0;">&quot;;</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; AAA.fun_3=function(){</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; console.log(&quot;fun_3-[</span><span style="color:#FF0000;"><span style="background-color:#F0FFF0;">静态方法</span></span><span style="background-color:#F0FFF0;">/工具函数]&quot;);</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; }</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; ------------------------------------</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; AAA={</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; prop_3a:&quot;</span><span style="color:#FF0000;"><span style="background-color:#F0FFF0;">静态属性2</span></span><span style="background-color:#F0FFF0;">_(对象字面量)&quot;,</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; fun_3a:function(){</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(&quot;fun_3a-[</span><span style="color:#FF0000;"><span style="background-color:#F0FFF0;">静态方法2</span></span><span style="background-color:#F0FFF0;">/工具函数]_(对象字面量)&quot;);</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; }</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; }</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; ------------------------------------</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; //原型</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; AAA.prototype.prop_4=&quot;</span><span style="color:#FF0000;"><span style="background-color:#F0FFF0;">原型属性</span></span><span style="background-color:#F0FFF0;">&quot;;</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; AAA.prototype.fun_4=function(){</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; console.log(&quot;fun_4-[</span><span style="color:#FF0000;"><span style="background-color:#F0FFF0;">原型方法</span></span><span style="background-color:#F0FFF0;">/公有方法]&quot;);</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; this.fun_2(); //共有方法的调用</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; }</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; ------------------------------------</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; //原型重写</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; AAA.prototype={</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; prop_4a:&quot;</span><span style="color:#FF0000;"><span style="background-color:#F0FFF0;">原型属性2</span></span><span style="background-color:#F0FFF0;">_(原型重写)&quot;,</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; fun_4a:function(){</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(&quot;fun_4a-[</span><span style="color:#FF0000;"><span style="background-color:#F0FFF0;">原型方法2</span></span><span style="background-color:#F0FFF0;">/公有方法]_(原型重写)&quot;);</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; }</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; }</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; ------------------------------------</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; var x = new AAA(); //实例化 x</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp;&nbsp;</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; x.prop_5=&quot;</span><span style="color:#FF0000;"><span style="background-color:#F0FFF0;">实例属性</span></span><span style="background-color:#F0FFF0;">&quot;;</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; x.fun_5=function(){</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; console.log(&quot;fun_5-[</span><span style="color:#FF0000;"><span style="background-color:#F0FFF0;">实例方法</span></span><span style="background-color:#F0FFF0;">]&quot;);</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; }</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; ------------------------------------</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; var y = new AAA(); //实例化 y</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; ------------------------------------</span></p>\r\n\r\n<p style="line-height:16px;"><span style="background-color:#F0FFF0;">}());</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>参考：<a href="https://www.cnblogs.com/xiongzaiqiren/p/6733985.html" target="_blank">https://www.cnblogs.com/xiongzaiqiren/p/6733985.html</a></p>\r\n\r\n<p>其他参考：<a href="http://www.runoob.com/w3cnote/es6-class.html" target="_blank">http://www.runoob.com/w3cnote/es6-class.html</a></p>\r\n\r\n<p><br />\r\n-----------------------</p>\r\n\r\n<p># 类相关<br />\r\n&nbsp; &nbsp; + &mdash;&mdash;&mdash;&mdash;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; --构造函数名字头个字母大写；<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; --静态方法是在处理性质上相似的一些公用函数，抽象出来，绑定到一个类下来进行管理。js中可以在一个构造器上加它自己的属性方法来作为静态方法和静态属性。<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; --实例方法和静态类似，也是同一批操作的集合，不过实例方法会有相同的处理重心this，也就是说，实例方法是当前对象this的某个动作或行为，特殊强调当前的上下文this<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; --静态方法更像是工具集，是通用功能的抽取，实例方法更强调上下文的服务者，是一类对象特征的抽象</p>\r\n\r\n<p># 属性<br />\r\n&nbsp; &nbsp; + 相关<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; --静态属性是全局的，原型属性是实例公有的，实例属性是各个实例所独有的</p>\r\n\r\n<p># 特权方法和公有方法：<br />\r\n&nbsp; &nbsp; * 相同点：<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; --都可以在构造函数外部公开访问（实例都可以访问）<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; --都可以访问公有属性&nbsp;<br />\r\n&nbsp; &nbsp; * 不同点<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; --每个实例都拥有一份特权方法的副本（除在单例中使用外，需要考虑内存），而公有方法为所有实例共享同一个引用地址<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(x.fun_2 === y.fun_2); //false - 两实例的特权方法的引用不相同, 说明在对象实例化的时特权方法被重新创建&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; console.log(x.fun_4 === y.fun_4); //true<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; --特权方法可以访问私有属性和私有方法；公有方法不能；<br />\r\n&nbsp; &nbsp; + 其他<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; --公有方法即原型方法<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; --特权方法作为构造函数的接口，公有方法可以通过特权方法访问私有属性和方法</p>\r\n\r\n<p><br />\r\n__________________________________________________________________</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>当代码var zhang = new Person(&quot;ZhangSan&quot;, &quot;man&quot;)执行时，其内部做了如下几件事情：</p>\r\n\r\n<p>* 创建一个空白对象（new Object()）。<br />\r\n* 拷贝Person.prototype中的属性（键值对）到这个空对象中（我们前面提到，内部实现时不是拷贝而是一个隐藏的链接）。<br />\r\n* 将这个对象通过this关键字传递到构造函数中并执行构造函数。<br />\r\n* 将这个对象赋值给变量zhang。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<hr />\r\n<p style="font-family: &quot;Microsoft Yahei&quot;, arial, Tahoma, sans-serif;">BaseClass.f1 = function(){//定义静态方法 &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp;alert(&#39; This is a static method &#39;); &nbsp;<br />\r\n} &nbsp;<br />\r\n-静态方法不能被实例对象调用</p>\r\n\r\n<p style="font-family: &quot;Microsoft Yahei&quot;, arial, Tahoma, sans-serif;">--实例方法需要先实例化，再调用</p>\r\n\r\n<p style="font-family: &quot;Microsoft Yahei&quot;, arial, Tahoma, sans-serif;"><br />\r\n--直接定义在实例上的变量的优先级要高于定义在&ldquo;this&rdquo;上的，<br />\r\n而定义在&ldquo;this&rdquo;上的又高于 prototype定义的变量。<br />\r\n即直接定义在实例上的变量会覆盖定义在&ldquo;this&rdquo;上和prototype定义的变量，<br />\r\n定义在&ldquo;this&rdquo;上的会覆盖prototype定义的变量</p>\r\n\r\n<p style="font-family: &quot;Microsoft Yahei&quot;, arial, Tahoma, sans-serif;"><br />\r\n--内部方法<br />\r\nvar BaseClass = function() { &nbsp;<br />\r\n&nbsp; &nbsp; var method2 = function() { &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; alert(&quot;call Internal method&quot;); &nbsp;&nbsp;<br />\r\n&nbsp; &nbsp; }; &nbsp;<br />\r\n&nbsp; &nbsp; this.method3 = function(){ &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; method2(); &nbsp;<br />\r\n&nbsp; &nbsp; } &nbsp;<br />\r\n};</p>\r\n\r\n<p style="font-family: &quot;Microsoft Yahei&quot;, arial, Tahoma, sans-serif;">&nbsp;</p>\r\n\r\n<p style="font-family: &quot;Microsoft Yahei&quot;, arial, Tahoma, sans-serif;">--jquery它的方法都是实例方法，它的工具函数都是静态方法<br />\r\n实例调用方式$(&quot;body&quot;).each(//fun...); 静态方法 $.each($(&quot;body&quot;),fun);</p>\r\n\r\n<p style="font-family: &quot;Microsoft Yahei&quot;, arial, Tahoma, sans-serif;"><br />\r\n--静态方法是类直接调用，实例方法要new 出来的对象才能调用。</p>\r\n\r\n<p style="font-family: &quot;Microsoft Yahei&quot;, arial, Tahoma, sans-serif;">&nbsp;</p>\r\n\r\n<hr />\r\n<p>&nbsp;</p>\r\n\r\n<p>类的三大特性(封装，继承，多态)</p>\r\n\r\n<p><a href="http://blog.csdn.net/jiangwei0910410003/article/details/41707161" target="_blank">http://blog.csdn.net/jiangwei0910410003/article/details/41707161</a></p>\r\n\r\n<p>js中实现多态</p>\r\n\r\n<p><a href="http://blog.csdn.net/xiebaochun/article/details/38749953" target="_blank">http://blog.csdn.net/xiebaochun/article/details/38749953</a></p>\r\n\r\n<p style="line-height: 14px;">js面向对象三大特征之多态笔记</p>\r\n\r\n<p><a href="http://www.cnblogs.com/hezhi/p/4578763.html" target="_blank">http://www.cnblogs.com/hezhi/p/4578763.html</a></p>\r\n', '', '2016-11-22 07:12:29', '2019-02-17 11:59:42', 61),
(159, '', 0, '继承', NULL, '', '<p>&nbsp;&nbsp; &nbsp;继承：<br />\r\n&nbsp;&nbsp; &nbsp;------------------------------------</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;//父类[基类/超类] (下面通用部分)<br />\r\n&nbsp;&nbsp; &nbsp;------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;var Animal = function(){<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp;this.name = &#39;animal&#39;;<br />\r\n&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;Animal.prototype.say = function(){<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp;console.log(&#39;Animal here&#39;);<br />\r\n&nbsp;&nbsp; &nbsp;}</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<hr />\r\n<p>&nbsp;</p>\r\n\r\n<p><span style="color: rgb(255, 0, 0);">抽象类</span>：不能产生对象，它只能充当父类;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- 如果一个类包含了一个抽象方法，那么该类也必须是抽象类；</p>\r\n\r\n<p>- 任何子类必须重写抽象方法，除非子类本身也是抽象类；</p>\r\n\r\n<p>- 用关键字abstract修饰类的时候，表示这个类是抽象类；</p>\r\n\r\n<p>-------&nbsp;</p>\r\n\r\n<p>- 有抽象方法的类一定是抽象类；</p>\r\n\r\n<p>- 抽象类不一定有抽象方法；</p>\r\n\r\n<p>- 抽象类除了类有abstract修饰符，它内部跟普通类一样，可以有：属性、构造、已经实现的方法；</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<hr />\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;***************<br />\r\n&nbsp;&nbsp; &nbsp;***继承方法:***<br />\r\n&nbsp;&nbsp; &nbsp;***************</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;# 原型链继承：<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var Dog = function(){}<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;Dog.prototype = new Animal();<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;Dog.prototype.name = &#39;dog&#39;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;//实例<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var doge = new Dog();<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;console.log(doge.name) &nbsp;//&#39;dog&#39;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;doge.say() &nbsp;//&#39;Animal here&#39;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;*注：原型链赋值给子类prototype等同于重写，后面不能再重写，否则被覆盖从而无法实现继承<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;*注：原型链赋值之后才能添加或覆盖方法<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;*弊：没法把子类的属性赋值到父类<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;*弊：子类prototype中的对象(如&#39;[&quot;red&quot;,&quot;blue&quot;]&#39;)赋值时为引用关系，两端共享一个引用地址，实例可以修改原型内的属性</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;# 构造继承（借用方法）：<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var Dog = function(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;Animal.call(this);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;this.name = &#39;dog&#39;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;//实例<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var doge = new Dog();<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;console.log(doge.name) //&#39;dog&#39;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;doge.say() //error;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;*弊：只能继承属性，不能继承prototype内的方法</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;# 实例继承：<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var Dog = function(){&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;var dog = new Animal();<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;dog.name = &#39;dog&#39;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;return dog;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;//实例<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var doge = new Dog();<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;console.log(doge.name) &nbsp;//&#39;dog&#39;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;doge.say() &nbsp;//&#39;Animal here&#39;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;console.log(doge instanceof Animal) &nbsp;// true<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;console.log(doge instanceof Dog) &nbsp;// false</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;# 拷贝继承（暴力继承）：<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var Dog = function(){&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;var animal = new Animal();<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;for(var attr in animal){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;this[attr] = animal[attr];<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;this.name = &#39;dog&#39;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;//实例<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var doge = new Dog();<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;console.log(doge.name) //&#39;dog&#39;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;doge.say() //&#39;Animal here&#39;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;console.log(doge instanceof Animal) // false<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;console.log(doge instanceof Dog) // true</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;# 组合继承：<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var Dog = function(){&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp;Animal.call(this, arguments);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp;this.name = &#39;dog&#39;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;Dog.prototype = new Animal();<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;//实例<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var doge = new Dog();<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;console.log(doge.name) //&#39;dog&#39;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;doge.say() //&#39;Animal here&#39;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;console.log(doge instanceof Dog) // true<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;console.log(doge instanceof Animal) // true<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;*注：原型链继承+借用继承，完美实现继承</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;# Object.create()实现继承：<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var Child = Object.create(Animal, {<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;name: { value: &quot;Benjamin&quot;},<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;url : { value: &quot;http://www.baidu.com&quot;}<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;});<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;var SubChild = Object.create(Child, {<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;name: {value: &quot;zuojj&quot;},<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;sex : {value: &quot;male&quot;}<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;})</p>\r\n', '', '2016-11-22 07:12:51', '2018-12-27 07:21:48', 61),
(160, '', 0, '闭包', NULL, '', '<p>用处：</p>\r\n\r\n<p>------------------------</p>\r\n\r\n<p>- 存循环的索引值</p>\r\n\r\n<p>- 做私有变量计数器</p>\r\n\r\n<p>- 封装源码</p>\r\n\r\n<p>- 实现函数节流（throttle）和函数去抖（debounce）<br />\r\n<a href="http://www.sohu.com/a/194411035_608275" target="_blank">http://www.sohu.com/a/194411035_608275</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<hr />\r\n<p>&nbsp;</p>\r\n\r\n<p>利用js的垃圾回收原理来理解闭包(Closure)问题：<br />\r\nhttp://www.cnblogs.com/tyhmj/archive/2012/02/29/2373626.html<br />\r\n内存泄露以及循环引用解析：<br />\r\nhttp://www.2cto.com/kf/201507/417319.html<br />\r\n彻底弄懂js循环中的闭包问题:<br />\r\nhttp://www.cnblogs.com/hljmly/p/5448134.html</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;闭包特性：<br />\r\n&nbsp;&nbsp; &nbsp;------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;* 函数嵌套函数<br />\r\n&nbsp;&nbsp; &nbsp;* 内部函数可以引用外部函数的参数和变量<br />\r\n&nbsp;&nbsp; &nbsp;* 此参数和变量不会被垃圾回收机制所回收</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;好处：<br />\r\n&nbsp;&nbsp; &nbsp;------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;* 希望一个变量能长期驻扎在内存当中<br />\r\n&nbsp;&nbsp; &nbsp;* 避免全局变量的污染<br />\r\n&nbsp;&nbsp; &nbsp;* 私有成员的存在</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;用法：<br />\r\n&nbsp;&nbsp; &nbsp;------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;* 3-1 模块化代码<br />\r\n&nbsp;&nbsp; &nbsp;* 3-2 在循环中直接找到对应元素的索引</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;注意：<br />\r\n&nbsp;&nbsp; &nbsp;------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;* 4-1 ie下会引发内存泄漏</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;实例：<br />\r\n&nbsp;&nbsp; &nbsp;------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;/*a为全局变量，可累加*/<br />\r\n&nbsp;&nbsp; &nbsp;var a=1;<br />\r\n&nbsp;&nbsp; &nbsp;function aaa(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;a++;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;alert(a);<br />\r\n&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;aaa(); //2<br />\r\n&nbsp;&nbsp; &nbsp;aaa(); //3<br />\r\n&nbsp;&nbsp; &nbsp;alert(a); //能访问<br />\r\n&nbsp;&nbsp; &nbsp;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;/*为避免全局变量的污染，a为局部变量，但不能累加*/<br />\r\n&nbsp;&nbsp; &nbsp;function aaa(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var a=1;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;a++;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;alert(a);<br />\r\n&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;aaa(); //2<br />\r\n&nbsp;&nbsp; &nbsp;aaa(); //2<br />\r\n&nbsp;&nbsp; &nbsp;alert(a); //不能访问<br />\r\n&nbsp;&nbsp; &nbsp;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;/*a为局部变量，同时也可以累加，这就是闭包的好处*/&nbsp;&nbsp; &nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;function aaa(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var a=1;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return function(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;a++;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;alert(a);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;var b=aaa();<br />\r\n&nbsp;&nbsp; &nbsp;b(); //2<br />\r\n&nbsp;&nbsp; &nbsp;b(); //3<br />\r\n&nbsp;&nbsp; &nbsp;alert(a); //不能访问<br />\r\n&nbsp;&nbsp; &nbsp;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;3-1&mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;/*把上一例的闭包代码模块化*/<br />\r\n&nbsp;&nbsp; &nbsp;var aaa=(function(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var a=1;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return function(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;a++;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;alert(a);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;})();<br />\r\n&nbsp;&nbsp; &nbsp;aaa(); //2<br />\r\n&nbsp;&nbsp; &nbsp;aaa(); //3<br />\r\n&nbsp;&nbsp; &nbsp;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash; &nbsp; &mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;/*模块化代码的一种模式*/<br />\r\n&nbsp;&nbsp; &nbsp;var aaa=(function(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var a=1;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;function bbb(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;a++;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;alert(a);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;function ccc(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;a++;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;alert(a);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return{<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;b:bbb, //私有成员<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;c:ccc<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;})();<br />\r\n&nbsp;&nbsp; &nbsp;aaa.b(); //2<br />\r\n&nbsp;&nbsp; &nbsp;aaa.c(); //3<br />\r\n&nbsp;&nbsp; &nbsp;alert(a); //不能访问<br />\r\n&nbsp;&nbsp; &nbsp;alert(bbb); //不能访问<br />\r\n&nbsp;&nbsp; &nbsp;alert(ccc); //不能访问<br />\r\n&nbsp;&nbsp; &nbsp;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;3-2&mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;&lt;li&gt;1111111111&lt;/li&gt;&lt;li&gt;1111111111&lt;/li&gt;&lt;li&gt;1111111111&lt;/li&gt;<br />\r\n&nbsp;&nbsp; &nbsp;window.onload=function(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var aLi=document.getElementsByTagName(&quot;li&quot;);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;for(var i=0;i &lt; aLi.length;i++){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;aLi[i].onclick=function(){ //事件执行前for循环已执行完，i已累加成2了<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;alert(i);&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash; &nbsp; &mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;window.onload=function(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var aLi=document.getElementsByTagName(&quot;li&quot;);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;for(var i=0;i &lt; aLi.length;i++){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;(function(i){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;aLi[i].onclick=function(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;alert(i);&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;})(i);&nbsp;&nbsp; &nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash; &nbsp; &mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;window.onload=function(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var aLi=document.getElementsByTagName(&quot;li&quot;);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;for(var i=0;i &lt; aLi.length;i++){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;aLi[i].onclick=(function(i){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return function(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;alert(i);&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;})(i);&nbsp;&nbsp; &nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;4-1&mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;//内存泄漏：变量没被回收而一直驻扎在cpu里占用内存，只有关闭浏览器才会被释放<br />\r\n&nbsp;&nbsp; &nbsp;//导致原因：闭包内保存了外部变量的引用，而变量本身又引用了闭包，导致循环引用<br />\r\n&nbsp;&nbsp; &nbsp;window.onload=function(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var oDiv=document.getElementById(&quot;div1&quot;);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oDiv.onclick=function(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;alert(oDiv.id);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash; &nbsp; &mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;//防止内存泄漏方法一：<br />\r\n&nbsp;&nbsp; &nbsp;window.onload=function(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var oDiv=document.getElementById(&quot;div1&quot;);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oDiv.onclick=function(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;alert(oDiv.id);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;window.onunload=function(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oDiv.onclick=null;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash; &nbsp; &mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;//防止内存泄漏方法二：<br />\r\n&nbsp;&nbsp; &nbsp;window.onload=function(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var oDiv=document.getElementById(&quot;div1&quot;);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var id=oDiv.id;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oDiv.onclick=function(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;alert(id);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oDiv=null;<br />\r\n&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\nJavascript内存回收机制:<br />\r\n--当函数调用完之后内部的变量所占用的内存控件就被释放掉<br />\r\n--如果一个对象不再被引用，那么这个对象就会被GC回收。如果两个对象互相引用，而不再被第3者所引用，那么这两个互相引用的对象也会被回收。<br />\r\n--闭包调用时执行完后闭包还是被外部的变量所引用，所以此闭包没被回收，它所引用的外部变量也就没被释放了?<br />\r\n&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;<br />\r\n例：<br />\r\nfunction a(){<br />\r\n&nbsp;&nbsp; &nbsp;var x=10;<br />\r\n&nbsp;&nbsp; &nbsp;console.log(&quot;a-&gt;x:&quot;+x);<br />\r\n&nbsp;&nbsp; &nbsp;return function b(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;x++;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;console.log(&quot;b-&gt;x:&quot;+x);<br />\r\n&nbsp;&nbsp; &nbsp;}<br />\r\n}<br />\r\n//(1)<br />\r\n&nbsp;&nbsp; &nbsp;var ii=a();<br />\r\n&nbsp;&nbsp; &nbsp;ii();<br />\r\n&nbsp;&nbsp; &nbsp;var jj=a();<br />\r\n&nbsp;&nbsp; &nbsp;jj();<br />\r\n&nbsp;&amp;<br />\r\n&nbsp;//(2)<br />\r\n&nbsp;&nbsp;&nbsp; &nbsp;var ii=a();<br />\r\n&nbsp;&nbsp; &nbsp;ii(); //这执行完后ii等于b()的引用；变量x在b()已有定义并累计到了11<br />\r\n&nbsp;&nbsp; &nbsp;ii(); //此时执行的是上一部最后引用的b()，x累计到12；执行完后ii又再等于b()的引用</p>\r\n\r\n<p>---------------------------------</p>\r\n\r\n<p><br />\r\n# 测试<br />\r\nfunction exp1(){<br />\r\n&nbsp; &nbsp; var name=&quot;name&quot;<br />\r\n&nbsp; &nbsp; var age=100<br />\r\n&nbsp; &nbsp; return function(){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; return name;<br />\r\n&nbsp; &nbsp; }<br />\r\n}<br />\r\nconsole.dir(exp1())<br />\r\n&lt;/xmp&gt;<br />\r\n&lt;img src=&quot;../for_resource/closure.png&quot; /&gt;</p>\r\n\r\n<hr />\r\n<p>相关实例：</p>\r\n\r\n<div>/*for循环内定义的变量i为全局*/</div>\r\n\r\n<div>for (var i = 0; i &lt; 8; i++) {</div>\r\n\r\n<div>console.log(i); // 0 ~ 7</div>\r\n\r\n<div>}</div>\r\n\r\n<div>console.log(i); &nbsp;// 8</div>\r\n\r\n<div>-----------------------------</div>\r\n\r\n<div>var b=[];</div>\r\n\r\n<div>for(var i=0; i&lt;5; i++){</div>\r\n\r\n<div>b[i]=function() {</div>\r\n\r\n<div>console.log(i);</div>\r\n\r\n<div>}</div>\r\n\r\n<div>}</div>\r\n\r\n<div>b[2](); // 4</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>/*闭包*/</div>\r\n\r\n<div>var b=[];</div>\r\n\r\n<div>for(var i=0; i&lt;5; i++) {</div>\r\n\r\n<div>b[i] = (function (i) {</div>\r\n\r\n<div>return function () {</div>\r\n\r\n<div>console.log(i);</div>\r\n\r\n<div>}</div>\r\n\r\n<div>})(i);</div>\r\n\r\n<div>}</div>\r\n\r\n<div>b[2](); &nbsp;// 2</div>\r\n\r\n<div>\r\n<hr />\r\n<p>面试题：</p>\r\n\r\n<div>var V = function(){</div>\r\n\r\n<div>B = function () {n++; console.log(n);}</div>\r\n\r\n<div>var n = 999;</div>\r\n\r\n<div>return function C() {</div>\r\n\r\n<div>console.log(n);</div>\r\n\r\n<div>}</div>\r\n\r\n<div>}();</div>\r\n\r\n<div>V(); //999</div>\r\n\r\n<div>B();</div>\r\n\r\n<div>V(); //1000</div>\r\n\r\n<div>--------------------------</div>\r\n\r\n<div>\r\n<div>var x = {</div>\r\n\r\n<div>a : 999,</div>\r\n\r\n<div>b : function () {</div>\r\n\r\n<div>this.a++;</div>\r\n\r\n<div>},</div>\r\n\r\n<div>c : function () {</div>\r\n\r\n<div>console.log(this.a);</div>\r\n\r\n<div>}</div>\r\n\r\n<div>};</div>\r\n\r\n<div>x.c(); //999</div>\r\n\r\n<div>x.b();</div>\r\n\r\n<div>x.c(); //1000</div>\r\n</div>\r\n\r\n<div>--------------------------</div>\r\n\r\n<div>function AA(){</div>\r\n\r\n<div>this.x = 999;</div>\r\n\r\n<div>}</div>\r\n\r\n<div>AA.prototype.y = function () {</div>\r\n\r\n<div>this.x++;</div>\r\n\r\n<div>console.log(this.x);</div>\r\n\r\n<div>}</div>\r\n\r\n<div>var c = new AA();</div>\r\n\r\n<div>c.y(); // 1000</div>\r\n\r\n<div>c.y(); // 1001</div>\r\n</div>\r\n', '', '2016-11-22 07:13:20', '2019-03-24 09:04:21', 61);
INSERT INTO `point` (`id`, `pid`, `sort`, `title`, `attr`, `description`, `extend`, `subFieldSet`, `createdAt`, `updatedAt`, `lineId`) VALUES
(161, '', 0, '事件监听', NULL, '', '<p>&nbsp; &nbsp; 监听事件(注意事项)：<br />\r\n&nbsp; &nbsp; ------------------------------------<br />\r\n&nbsp; &nbsp; * 当同一个对象使用同一事件触发多个方法时，后一个方法会把前一个方法覆盖掉，而用事件监听则不会有覆盖的现象，每个绑定的事件都会被执行。<br />\r\n&nbsp; &nbsp; * 事件监听会有一个冒泡的问题，即事件会从事件源一直向上级元素传递<br />\r\n&nbsp; &nbsp; * 可使用事件委托(事件代理)利用冒泡的原理来提升性能</p>\r\n\r\n<p><br />\r\n&nbsp; &nbsp; 事件兼容问题：<br />\r\n&nbsp; &nbsp; ------------------------------------<br />\r\n&nbsp; &nbsp; 获取事件对象： IE用window.event || 其他用event<br />\r\n&nbsp; &nbsp; 取消事件默认操作： IE用&#39;事件对象.returnValue=false&#39;, || 其他用&#39;事件对象.preventDefault()&#39;<br />\r\n&nbsp; &nbsp; 获取事件源： IE用&#39;事件对象.srcElement&#39; || 其他用&#39;事件对象.target&#39;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\n&nbsp; &nbsp; ***<strong><span style="color:#B22222;">JS-监听事件</span></strong>:***<br />\r\n&nbsp; &nbsp; ******************</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; js添加监听事件：<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; ------------------------------------<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 标准：document.getElementById(&quot;AAA&quot;).<span style="color:#FF0000;">addEventListener</span>(&quot;click&quot;, evFun);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; IE：document.getElementById(&quot;AAA&quot;).<span style="color:#FF0000;">attachEvent</span>(&quot;click&quot;, evFun);&nbsp;</p>\r\n\r\n<p><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; js移除监听事件：<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; ------------------------------------<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 标准：document.getElementById(&quot;AAA&quot;).<span style="color:#FF0000;">removeEventListener</span>(&quot;click&quot;, evFun);&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; IE：document.getElementById(&quot;AAA&quot;).<span style="color:#FF0000;">detachEvent</span>(&quot;click&quot;, evFun);</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; js主动触发事件：<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; ------------------------------------<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 标准：document.getElementById(&quot;AAA&quot;).<span style="color:#FF0000;">dispatchEvent</span>(&quot;click&quot;);&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; IE：document.getElementById(&quot;AAA&quot;).click();</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\n&nbsp; &nbsp; ***<strong><span style="color:#B22222;">JQ-监听事件</span></strong>:***<br />\r\n&nbsp; &nbsp; *****************</p>\r\n\r\n<p><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; <span style="color:#FF0000;">bind</span>():<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; ------------------------------------<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; $(&quot;button&quot;).bind(&quot;click&quot;,function(){...});<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; //绑定多个事件<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; $(&quot;button&quot;).bind({<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; mouseover:function(){$(&quot;body&quot;).css(&quot;background-color&quot;,&quot;red&quot;);},&nbsp;&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; mouseout:function(){$(&quot;body&quot;).css(&quot;background-color&quot;,&quot;black&quot;);}&nbsp;&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; });&nbsp; &nbsp;&nbsp;</p>\r\n\r\n<p><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; <span style="color:#FF0000;">delegate</span>():<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; ------------------------------------<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; //事件代理：通过监听父级节点来实现监听子节点的功能<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; $(&quot;ul&quot;).delegate(&quot;li&quot;,&quot;click&quot;,function(){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;$(this).toggle();<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; });</p>\r\n\r\n<p><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; <span style="color:#FF0000;">live</span>():<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; ------------------------------------<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; $(&quot;ul li&quot;).live(&quot;click&quot;,function(){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;$(this).toggle();<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; });</p>\r\n\r\n<p><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; //以上三种方法(bind/delegate/live)在jQuery1.8之后都不推荐使用，官方在1.9时已经取消使用live()方法了，所以建议都使用on()方法。</p>\r\n\r\n<p><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; <span style="color:#FF0000;">on</span>():<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; ------------------------------------</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; $(&quot;li&quot;).on(&quot;click&quot;,function(){//常规绑定<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;$(this).toggle();<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; });</p>\r\n\r\n<p><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; $(&quot;ul&quot;).on(&quot;click&quot;,&quot;li&quot;,function(){//事件委托 （有指定selector）<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;$(this).toggle();<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; });</p>\r\n\r\n<p><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; $(&quot;button&quot;).on(&quot;mouseover mouseout&quot;,function(){ //多个事件绑定一个函数<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(this).toggleClass(&quot;aa&quot;);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; });</p>\r\n\r\n<p><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; $(&quot;button&quot;).on({&nbsp; //多个事件绑定不同函数<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; mouseover:function(){$(&quot;body&quot;).css(&quot;background-color&quot;,&quot;red&quot;);},&nbsp;&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; mouseout:function(){$(&quot;body&quot;).css(&quot;background-color&quot;,&quot;black&quot;);}&nbsp;&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; });</p>\r\n\r\n<p><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; $(&quot;button&quot;).click(function(){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(&quot;ul li&quot;).off(&quot;click&quot;); off()移除on()所绑定的事件<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; });</p>\r\n\r\n<p><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; $(&quot;p&quot;).on(&quot;click&quot;, {msg: &quot;You just clicked me!&quot;}, function(event){ // 也可以传递数据<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;alert(event.data.msg);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; })</p>\r\n\r\n<p><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; <span style="color:#FF0000;">one</span>():<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; ------------------------------------<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; //事件只操作一次<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; $(&quot;ul li&quot;).one(&quot;click&quot;,function(){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;$(this).hide();<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; });</p>\r\n\r\n<p><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; <span style="color:#FF0000;">trigger</span>():<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; ------------------------------------<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; //触发所绑定的事件<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; $(function(){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; function myFun (ev) { ... }<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(&quot;p&quot;).on(&quot;myEvent&quot;, myFun);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(&quot;button&quot;).click(function(){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(&quot;p&quot;).trigger(&quot;myEvent&quot;,myFun);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; });<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; });</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<hr />\r\n<p>&nbsp;</p>\r\n\r\n<p>- obj.on(&#39;click&#39;,fun1) &nbsp;+ &nbsp;obj.on(&#39;click&#39;,fun2) &nbsp;//同个元素click事件可执行多个回调</p>\r\n\r\n<p>- obj.off(&#39;click&#39;) - 解绑obj点击事件的所有回调</p>\r\n\r\n<p>- obj.off(&#39;clikc&#39;,fun1) - 只解绑点击事件的fun1回调</p>\r\n\r\n<p>-&nbsp;$ul.on(&quot;touchstart touchmove touchend&quot;,&#39;&#39;,function(e){...}); -&nbsp;多个事件一起监听</p>\r\n', '', '2016-11-22 07:16:15', '2018-12-27 06:57:26', 62),
(162, '', 0, '原生JS操作DOM', NULL, '', '<p>DOM节点类型：<br />\r\n-----------</p>\r\n\r\n<p style="margin-left: 40px;">--文档 Document<br />\r\n--元素 Element<br />\r\n--属性 Attribute<br />\r\n--文本 Text<br />\r\n--注释 Comment</p>\r\n\r\n<p>--------------------------------------------------------------------------</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\n方法：<br />\r\n-----------</p>\r\n\r\n<p>getElementById(); &nbsp;//ID<br />\r\ngetElementsByName(); &nbsp;//name属性<br />\r\ngetElementsByTagName(); &nbsp;//标签名<br />\r\ngetElementsByClassName(); &nbsp;//类名</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>querySelectorAll(); //css选择器[返回匹配的所有元素]<br />\r\nquerySelector(); //css选择器[返回匹配的第一个元素]</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>getAttribute();<br />\r\nsetAttribute();<br />\r\nhasAttribute();<br />\r\nremoveAttribute();<br />\r\ncreateAttribute() &nbsp;//创建属性节点。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>insertAdjacentText(); //&nbsp;新增文本内容，第一个参数为beforebegin | afterbegin | beforeend | afterend<br />\r\ninsertAdjacentHTML();&nbsp; //同上</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>createElement()&nbsp;&nbsp; &nbsp; //创建元素节点。<br />\r\ncreateTextNode() &nbsp;//创建文本节点。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>insertBefore()</p>\r\n\r\n<p>appendChild()<br />\r\nremoveChild();<br />\r\nreplaceChild();<br />\r\ncloneNode();<br />\r\nhasChildNodes() &nbsp;//返回一个布尔值，指示元素是否有子元素</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>createDocumentFragment(); //<span style="font-size:12px;"><span style="font-family: &quot;Open Sans&quot;, arial, x-locale-body, sans-serif; background-color: rgb(255, 255, 255);">创建一个新的空白的文档片段</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>--------------------------------------------------------------------------</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\n属性：<br />\r\n-----------</p>\r\n\r\n<p>parentNode &nbsp;--父节点<br />\r\nchildNodes &nbsp;--子节点<br />\r\nfirstChild / lastChild &nbsp;--第一个和最后一个节点<br />\r\nnextSibling / previoursSibling &nbsp;--前一个和后一个节点</p>\r\n\r\n<p><br />\r\nnodeType &nbsp;--节点类型 （1~12）<br />\r\nno