etTop + &quot;px&quot;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;},30);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&mdash;&mdash; &nbsp; &mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var L=oDiv.offsetLeft; &nbsp;//把布局信息缓存起来再操作DOM,比上一例性能要好<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var T=oDiv.offsetTop;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;setInterval(function(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;L++;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;T++;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oDiv.style.left= L + &quot;px&quot;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oDiv.style.left= T + &quot;px&quot;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;},30);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&mdash;&mdash;3-4&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var oFrag=document.createDocumentFragment();<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;for(var i=0;i &lt; 5000;i++){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var oLi=document.createElement(&quot;li&quot;);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oFrag.appendChild(oLi); //把文档碎片都先存储到oFrag内<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oDiv.appendChild(oFrag); //再一次性操作DOM，比例2-1性能好<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;console.timeEnd(&quot;hi&quot;);<br />\r\n&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;</p>\r\n', '', '2016-11-22 07:23:12', '2016-11-21 19:23:12', 47),
(175, '', 0, 'requirejs基础', NULL, '', '<p>&nbsp;&nbsp; &nbsp;用requirejs的<span style="color:#B22222;">好处</span>：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;* 实现JS文件的异步加载，避免网页被堵塞<br />\r\n&nbsp;&nbsp; &nbsp;* 管理模块之间的依赖性，便于代码的编写和维护</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="line-height:14px;">&nbsp;&nbsp; &nbsp;实例<span style="color:#B22222;">目录结构</span>：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;test</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;js</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;app</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;a.js</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;b.js</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;lib</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;jquery1.7.js</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;jquery1.9.js</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;app.js</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;index.html</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;<span style="color:#B22222;">安装</span>：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;&lt;script src=&quot;js/require.js&quot; defer async=&quot;true&quot; data-main=&quot;js/app.js&quot;&gt;&lt;/script&gt;<br />\r\n&nbsp;&nbsp; &nbsp;//Async属性表明文件需要异步加载，IE不支持这个属性，只支持defer<br />\r\n&nbsp;&nbsp; &nbsp;//data-main 这个属性来加载初始化的JS文件</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;<span style="color:#B22222;">定义</span>模块：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;//如a.js如下:<br />\r\n&nbsp;&nbsp; &nbsp;define([&#39;依赖文件1&#39;,&#39;依赖文件2&#39;],function(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var add = function(x,y) {<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return x + y;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;};<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return {<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;add : add<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;});<br />\r\n&nbsp;&nbsp; &nbsp;//没有依赖项&#39;[]&#39;可空，也可省略</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;<span style="color:#B22222;">调用</span>：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;//如app.js如下:<br />\r\n&nbsp;&nbsp; &nbsp;require([&#39;app/a&#39;], function (a){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;console.log(a.add(1,1));<br />\r\n&nbsp;&nbsp; &nbsp;});<br />\r\n&nbsp;&nbsp; &nbsp;______________________________________________</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;//带错误回调：<br />\r\n&nbsp;&nbsp; &nbsp;require([&#39;app/a&#39;],function(a){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;alert(a);<br />\r\n&nbsp;&nbsp; &nbsp;},function(err){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;alert(err);<br />\r\n&nbsp;&nbsp; &nbsp;});<br />\r\n&nbsp;&nbsp; &nbsp;//载入错误时，不能执行第一个回调，转而执行第二个回调<br />\r\n&nbsp;&nbsp; &nbsp;______________________________________________</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;//载入失败回调中可以使用undef函数移除模块的注册<br />\r\n&nbsp;&nbsp; &nbsp;require([&#39;b&#39;],function($){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;//Do something with $ here<br />\r\n&nbsp;&nbsp; &nbsp;},function(err){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var failedId=err.requireModules &amp;&amp; &nbsp;err.requireModules[0]; //获得模块名<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if(failedId===&#39;b&#39;){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;requirejs.undef(failedId);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;})</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;/***<strong><span style="color:#B22222;">配置项:</span></strong>***/<br />\r\n&nbsp;&nbsp; &nbsp;*************</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;<span style="color:#800080;">baseUrl</span>：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;//基准目录 （没配置时由data-main指定）</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;<span style="color:#800080;">paths</span>：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;//映射不直接放在baseUrl下的文件目录，起始位置相对于baseUrl</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;<span style="color:#800080;">shim</span>：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;//为没有使用define定义的模块来声明依赖关系</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;<span style="color:#800080;">map</span>：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;//解决同一模块不同版本的问题</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;<span style="color:#800080;">config</span>：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;//将配置信息传给一个模块</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="line-height:14px;">&nbsp;&nbsp; &nbsp;实例：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;requierjs.config({</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;baseUrl:&#39;js/app&#39;,</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;paths:{</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;lib:&#39;../lib&#39;</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;},</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;shim:{</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&#39;a&#39;:[&#39;lib/jquery1.7&#39;]</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;},</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;map:{</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;a:{</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&#39;jquery&#39;:&#39;lib/jquery1.7.js&#39;</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;},</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;b:{</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&#39;jquery&#39;:&#39;lib/jquery1.9.js&#39;</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;},</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;config:{</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;c:{</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;size:&#39;large&#39;</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;},</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;d:{</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;color:&#39;red&#39;</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;});</span></p>\r\n\r\n<p style="line-height:12px;">&nbsp;</p>\r\n', '', '2016-11-22 07:23:54', '2016-12-06 17:57:46', 22),
(176, '', 0, 'AMD模块规范', NULL, '', '<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;第一种：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;<span style="color:#008080;">define(function() {&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return {&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;mix: function(source, target) {...}&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;};&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;});</span></p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;第二种：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; <span style="color:#808080;">&nbsp;//有依赖项</span><br />\r\n&nbsp;&nbsp; &nbsp;<span style="color:#008080;">define([&#39;data&#39;, &#39;ui&#39;], function(data, ui) {&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</span><span style="color:#808080;">// init here&nbsp;</span><br />\r\n<span style="color:#008080;">&nbsp;&nbsp; &nbsp;});</span></p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;第三种：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; <span style="color:#A9A9A9;">&nbsp;</span><span style="color:#808080;">//直接一个对象</span><br />\r\n&nbsp;&nbsp; &nbsp;<span style="color:#008080;">define({&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;data: [],&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;ui: []&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;});</span></p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;第四种：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;<span style="color:#808080;">//具名模块</span><br />\r\n&nbsp;&nbsp; &nbsp;<span style="color:#008080;">define(&#39;index&#39;, [&#39;data&#39;,&#39;base&#39;], function(data, base) {&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</span><span style="color:#808080;">// todo</span><span style="color:#008080;">&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;});</span></p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;第五种：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;<span style="color:#808080;">//包装模块</span><br />\r\n&nbsp;&nbsp; &nbsp;<span style="color:#008080;">define(function(require, exports, module) {&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var base = require(&#39;base&#39;);&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;exports.show = function() {&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</span><span style="color:#808080;">// todo with module base&nbsp;</span><br />\r\n<span style="color:#008080;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;} &nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;});</span><br />\r\n<span style="color:#A9A9A9;">&nbsp;&nbsp; &nbsp;</span><span style="color:#808080;">//使用require获取模块，使用exports或者module.exports导出API</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>=============================================================================================</p>\r\n\r\n<ul>\r\n	<li>对于第四种写法 具名模块写法我们并不推荐的，因为不书写模块名我们一样可以调用，且在合并代码的时候，我们也可以根据代码自动生成模块名，如果我们现在写死了模块名，当某个时候，b.js我要移动到其他目录时候，JS也要跟着改，所以代码维护方面不好，所以不建议书写模块名。</li>\r\n	<li>对于第五种写法，requireJS中也是支持的，通过内部调用require来处理依赖模块</li>\r\n</ul>\r\n', '', '2016-12-07 05:49:10', '2016-12-06 17:49:06', 22);
INSERT INTO `point` (`id`, `pid`, `sort`, `title`, `attr`, `description`, `extend`, `subFieldSet`, `createdAt`, `updatedAt`, `lineId`) VALUES
(177, '', 0, '媒体查询', NULL, '', '<p>&nbsp;&nbsp; &nbsp;媒体查询：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;@media : 指定样式表规则用于指定的设备类型。<br />\r\n&nbsp;&nbsp; &nbsp;指定媒体:<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;@media screen : 指定样式用于显示器<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;@media all : 指定样式用于所有媒体类型<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;@media print : 指定样式用于打印机</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;***特性***<br />\r\n&nbsp;&nbsp; &nbsp;**********</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;屏幕可见高度 - device-height ：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;@media screen and (device-height:800px){ &hellip; }<br />\r\n&nbsp;&nbsp; &nbsp;device-height : 定义输出设备的屏幕可见高度。<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;本特性接受min和max前缀，因此可以派生出min-device-height和max-device-height两个媒体特性。<br />\r\n&nbsp;&nbsp; &nbsp;width,height,device-width(前可加&#39;min-&#39;或&#39;max-&#39;)类同上；</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;可见区域高度是否大于或等于宽度 - orientation ：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;@media screen and (orientation:portrait){ &hellip; }<br />\r\n&nbsp;&nbsp; &nbsp;orientation : 定义输出设备中的页面可见区域高度是否大于或等于宽度。<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; portrait：指定输出设备中的页面可见区域高度大于或等于宽度<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; landscape：除portrait值情况外，都是landscape<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 本特性不接受min和max前缀。&nbsp;</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;宽高比率 - device-aspect-ratio ：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;@media screen and (device-aspect-ratio:4/3){ &hellip; }<br />\r\n&nbsp;&nbsp; &nbsp;device-aspect-ratio : 定义输出设备的屏幕可见宽度与高度的比率。<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;如常讲的显示器屏幕比率：4/3, 16/9, 16/10<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;本特性接受min和max前缀，因此可以派生出min-device-aspect-ratio和max-device-aspect-ratio两个媒体特性。&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;aspect-ratio类同上；</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;色彩数 - color ：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;@media screen and (color){ &hellip; }<br />\r\n&nbsp;&nbsp; &nbsp;color : 定义输出设备每一组彩色原件的个数。如果不是彩色设备，则值等于0<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;与色彩color不同，媒体特性color的取值只能是&lt;integer&gt;，用来表示色彩数。<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;本特性接受min和max前缀，因此可以派生出min-color和max-color两个媒体特性。&nbsp;</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;彩色查询表中的条目数 - color-index :<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;@media screen and (color-index){ &hellip; }<br />\r\n&nbsp;&nbsp; &nbsp;color-index : 定义在输出设备的彩色查询表中的条目数。如果没有使用彩色查询表，则值等于0<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;本特性接受min和max前缀，因此可以派生出min-color-index和max-color-index两个媒体特性。&nbsp;</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;单色原件个数 - monochrome ：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;@media screen and (monochrome){ &hellip; }<br />\r\n&nbsp;&nbsp; &nbsp;monochrome : 定义在一个单色框架缓冲区中每像素包含的单色原件个数。如果不是单色设备，则值等于0<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 本特性接受min和max前缀，因此可以派生出min-monochrome和max-monochrome两个媒体特性。&nbsp;</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;分辨率 - resolution ：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;@media screen and (resolution){ &hellip; }<br />\r\n&nbsp;&nbsp; &nbsp;resolution : 定义设备的分辨率。如：96dpi, 300dpi, 118dpcm<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 本特性接受min和max前缀，因此可以派生出min-resolution和max-resolution两个媒体特性。</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;扫描工序 - scan ：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;@media tv and (scan:progressive){ &hellip; }<br />\r\n&nbsp;&nbsp; &nbsp;scan : 定义电视类设备的扫描工序。<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 本特性不接受min和max前缀。</p>\r\n\r\n<p><br />\r\n&nbsp; &nbsp; 栅格 - grid ：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;@media all and (grid){ &hellip; }<br />\r\n&nbsp;&nbsp; &nbsp;grid : 用来查询输出设备是否使用栅格或点阵。<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 本特性不接受min和max前缀。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;注意事项：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;---手机端最小宽度以320px为准，最宽以640px为准；<br />\r\n&nbsp;&nbsp; &nbsp;---ipad分辨率：768px * 1024px；<br />\r\n&nbsp;&nbsp; &nbsp;---水平方向一般用百分比%，垂直方向可参照em单位(默认字体的倍数)；<br />\r\n&nbsp;&nbsp; &nbsp;---弹性图片: img{ max-width:100%};<br />\r\n&nbsp;&nbsp; &nbsp;---em(相对于父节点的font-size)，rem(相对于html的font-size);<br />\r\n&nbsp;&nbsp; &nbsp;---em一般用作排版元素适配设备的，字体一般不要作为排版元素 //rem=screen.width/20 ??<br />\r\n&nbsp;&nbsp; &nbsp;---ie8及以下版本不支持Media Query,兼容方法：<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; &lt;!--[if lt IE 9]&gt;<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&lt;script src=&quot;http://css3-mediaqueries-js.googlecode.com/svn/trunk/css3-mediaqueries.js&quot;&gt;&lt;/script&gt;<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; &lt;![endif]--&gt;<br />\r\n&nbsp;&nbsp; &nbsp;---常用取值：<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; ---240<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ---320<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; ---480 &nbsp;<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ---640<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; ---720&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ---960<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; ---1200<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ---1280<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; ---1440</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;其他：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;当页面通过屏幕呈现，非打印一类，并且屏幕不超过480px，则加载样式表：<br />\r\n&nbsp;&nbsp; &nbsp;&lt;link rel=&quot;stylesheet&quot; type=&quot;text/css&quot; media=&quot;screen and (max-device-width:480px)&quot; href=&quot;aaa.css&quot; /&gt;</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;参考：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;京东默认body{font-size:12px},.main{font-size:14px;line-height:1.25em};<br />\r\n&nbsp;&nbsp; &nbsp;@media(min-width:640px){.main{font-size:28px;}}<br />\r\n&nbsp;&nbsp; &nbsp;@media(min-width:480px){.main{font-size:21px;}}</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;实例：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;@media screen and(max-width:1024px){...}<br />\r\n&nbsp;&nbsp; &nbsp;@media screen and(max-width:980px){...}<br />\r\n&nbsp;&nbsp; &nbsp;@media screen and(max-width:720px){...}<br />\r\n&nbsp;&nbsp; &nbsp;@media screen and(max-width:640px){...}<br />\r\n&nbsp;&nbsp; &nbsp;@media screen and(max-width:320px){...}</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;未确定：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;@media (min-width: @grid-float-breakpoint){...} //??<br />\r\n&nbsp;&nbsp; &nbsp;@media screen and (-webkit-min-device-pixel-ratio: 0){...} //??<br />\r\n&nbsp;&nbsp; &nbsp;@media only screen and (min-device-width:320) and (max-device-width:480){...}<br />\r\n&nbsp;&nbsp; &nbsp;@media all and (-webkit-transform-3d){...}/* Android4.0下不识别该-webkit-transform-3d，使用它可做Android4.0下版本兼容 */<br />\r\n&nbsp;&nbsp; &nbsp;@media all and (transform-3d), (-webkit-transform-3d){} //??</p>\r\n', '', '2016-11-22 07:26:15', '2018-12-27 07:54:51', 65),
(178, '', 0, '适配原理', NULL, '', '<p>ppi&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; dpi &nbsp; &nbsp; mdpi &nbsp; &nbsp; hdpi &nbsp; &nbsp; xhdpi<br />\r\n调整设备宽度 &nbsp; &nbsp; &nbsp; 120 &nbsp; &nbsp; 180 &nbsp; &nbsp; &nbsp;240 &nbsp; &nbsp; &nbsp;320<br />\r\n默认缩放比&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 0.75&nbsp; &nbsp; &nbsp; &nbsp;1&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;1.5&nbsp; &nbsp; &nbsp; &nbsp;2<br />\r\n-------------------------------------------------------------------------------------&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<br />\r\ndp/pt(物理像素) -&gt; dpr(device Pixel Radio缩放比) -&gt; ppi(每英寸像素数) -&gt; px(逻辑像素)<br />\r\n-------------------------------------------------------------------------------------<br />\r\n分辨率说的是物理像素；<br />\r\n-------------------------------------------------------------------------------------<br />\r\n1px=(dpr)的平方*dp; //如I5的dpr为2，1px要用2*2dp来显示<br />\r\n-------------------------------------------------------------------------------------<br />\r\ni5(Retina屏)分辨率为640*1136，ppi算法：(640平方+1136平方)/4英寸=326ppi；按上表看i5默认缩放比为2；<br />\r\n-------------------------------------------------------------------------------------<br />\r\n&lt;meta name=&quot;viewport&quot; content=&quot;width=device-width,height=device-height,inital-scale=1.0,user-scalable=no;&quot;&gt;<br />\r\nios默认viewpoint为980，安卓不确定；一般移动开发viewport视口设置如上，宽高为设备的宽高，缩放比例为1；<br />\r\n-------------------------------------------------------------------------------------</p>\r\n\r\n<p>实例：<br />\r\n-----------<br />\r\n图片一比一显示在手机上会模糊，图片宽高应该对应上手机的物理像素，<br />\r\n如I5缩放比为2，1px要用2*2dp来显示，<br />\r\n所以要显示50*50的图片，要用实际100px*100px的图片，再样式设置成50px*50px显示；<br />\r\n-------------------------------------------------------------------------------------<br />\r\n--1像素边线在手机端显示不够细且有点模糊，样式设置：<br />\r\n.div{ position:relative;}<br />\r\n.div:after{ content:&#39;&#39;; position:absolute; top:-1px; left:0; width:100%; height:1px; border-top:#333 1px solid; -webkit-transform:scaleY(0.5);}<br />\r\n-------------------------------------------------------------------------------------</p>\r\n\r\n<p>&nbsp;</p>\r\n', '', '2016-11-22 07:26:38', '2018-12-27 08:19:48', 65),
(179, '', 0, 'touch事件', NULL, '', '<p>触摸事件：<br />\r\n-----------------------<br />\r\n---touchstart：触摸开始的时候触发<br />\r\n---touchmove：手指在屏幕上滑动的时候触发<br />\r\n---touchend：触摸结束的时候触发</p>\r\n\r\n<p><br />\r\n每个触摸事件都包括了三个触摸列表：<br />\r\n-----------------------<br />\r\n---touches：当前位于屏幕上的所有手指的列表。<br />\r\n---targetTouches：位于当前DOM元素上手指的列表。<br />\r\n---changedTouches：涉及当前事件手指的列表。</p>\r\n\r\n<p><br />\r\n每个列表里包含了对应的一系列触摸点，每个触摸点由包含了如下触摸信息（常用）：<br />\r\n-----------------------<br />\r\n---identifier：一个数值，唯一标识触摸会话（touch session）中的当前手指。一般为从0开始的流水号（android4.1，uc）<br />\r\n---target：DOM元素，是动作所针对的目标。<br />\r\n---pageX/pageX/clientX/clientY/screenX/screenY：一个数值，动作在屏幕上发生的位置（page包含滚动距离,client不包含滚动距离，screen则以屏幕为基准）。<br />\r\n---radiusX/radiusY/rotationAngle：画出大约相当于手指形状的椭圆形，分别为椭圆形的两个半径和旋转角度。</p>\r\n\r\n<p><br />\r\n-----------------------------------------------------------------------------------------<br />\r\na标签的四个伪类link，visited，active，hover是专为click事件设计的，所以在触屏网站中尽量不要使用它们。</p>\r\n', '', '2016-11-22 07:26:53', '2018-12-27 07:58:03', 65),
