(193, '', 0, '对象的声明和遍历', NULL, '', '<div>构造函数式 声明：</div>\r\n\r\n<div>----------------------------</div>\r\n\r\n<div>var x = new Object();</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>对象字面量表达法：</div>\r\n\r\n<div>----------------------------</div>\r\n\r\n<div>var x = { a:1, b:2 };</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>Object.create</div>\r\n\r\n<div>----------------------------</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>\r\n<hr />\r\n<p>&nbsp;</p>\r\n\r\n<p>对象遍历：</p>\r\n\r\n<p>----------------------------</p>\r\n</div>\r\n\r\n<div>var obj = {a: 111,b:222}</div>\r\n\r\n<div>for (var (key,index) in obj) {&nbsp; &nbsp;???</div>\r\n\r\n<div>&nbsp; &nbsp; ...</div>\r\n\r\n<div>}</div>\r\n', '', '2018-01-07 07:16:04', '2018-12-27 07:40:59', 58),
