(296, '', 0, '兼容', NULL, '', '<p><span style="color:#B22222;">drawImage跨域问题：</span></p>\r\n\r\n<p>-----------------------------------------------</p>\r\n\r\n<p><span style="font-size:14px;">drawImage()合并的图片如有异域图片会报错，网上找到的都是加下面代码来跨域：</span></p>\r\n\r\n<p><span style="font-size:14px;">img.setAttribute(&quot;crossOrigin&quot;, &#39;anonymous&#39;);</span></p>\r\n\r\n<p><span style="font-size:14px;">或 img.crossOrigin=&quot;anonymous&quot;</span></p>\r\n\r\n<p><span style="font-size:14px;">PC端可以跨域，但手机端（iphone6用到的所有浏览器）都无法下载图片而进入img.onerror()</span></p>\r\n\r\n<p><span style="font-size:14px;">最后用到把图片地址处理成同域的再通过一个php文件请求就跳过了跨域的问题</span></p>\r\n\r\n<p><span style="font-size:14px;">---------------------------------------</span></p>\r\n\r\n<p>// 地址转base64<br />\r\nfunction Base64() { &nbsp;<br />\r\n&nbsp; &nbsp; _keyStr = &quot;ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/=&quot;; &nbsp;<br />\r\n&nbsp; &nbsp; // public method for encoding &nbsp;<br />\r\n&nbsp; &nbsp; this.encode = function (input) { &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; var output = &quot;&quot;; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; var chr1, chr2, chr3, enc1, enc2, enc3, enc4; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; var i = 0; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; input = _utf8_encode(input); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; while (i &lt; input.length) { &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; chr1 = input.charCodeAt(i++); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; chr2 = input.charCodeAt(i++); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; chr3 = input.charCodeAt(i++); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; enc1 = chr1 &gt;&gt; 2; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; enc2 = ((chr1 &amp; 3) &lt;&lt; 4) | (chr2 &gt;&gt; 4); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; enc3 = ((chr2 &amp; 15) &lt;&lt; 2) | (chr3 &gt;&gt; 6); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; enc4 = chr3 &amp; 63; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if (isNaN(chr2)) { &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; enc3 = enc4 = 64; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; } else if (isNaN(chr3)) { &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; enc4 = 64; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; } &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; output = output + &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; _keyStr.charAt(enc1) + _keyStr.charAt(enc2) + &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; _keyStr.charAt(enc3) + _keyStr.charAt(enc4); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; } &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; return output; &nbsp;<br />\r\n&nbsp; &nbsp; } &nbsp;<br />\r\n&nbsp; &nbsp; this.decode = function (input) { &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; var output = &quot;&quot;; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; var chr1, chr2, chr3; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; var enc1, enc2, enc3, enc4; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; var i = 0; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; input = input.replace(/[^A-Za-z0-9+/=]/g, &quot;&quot;); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; while (i &lt; input.length) { &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; enc1 = _keyStr.indexOf(input.charAt(i++)); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; enc2 = _keyStr.indexOf(input.charAt(i++)); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; enc3 = _keyStr.indexOf(input.charAt(i++)); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; enc4 = _keyStr.indexOf(input.charAt(i++)); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; chr1 = (enc1 &lt;&lt; 2) | (enc2 &gt;&gt; 4); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; chr2 = ((enc2 &amp; 15) &lt;&lt; 4) | (enc3 &gt;&gt; 2); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; chr3 = ((enc3 &amp; 3) &lt;&lt; 6) | enc4; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; output = output + String.fromCharCode(chr1); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if (enc3 != 64) { &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; output = output + String.fromCharCode(chr2); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; } &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if (enc4 != 64) { &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; output = output + String.fromCharCode(chr3); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; } &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; } &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; output = _utf8_decode(output); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; return output; &nbsp;<br />\r\n&nbsp; &nbsp; } &nbsp;<br />\r\n&nbsp; &nbsp;<br />\r\n&nbsp; &nbsp; // private method for UTF-8 encoding &nbsp;<br />\r\n&nbsp; &nbsp; _utf8_encode = function (string) { &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; string = string.replace(/ /g,&quot; &quot;); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; var utftext = &quot;&quot;; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; for (var n = 0; n &lt; string.length; n++) { &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var c = string.charCodeAt(n); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if (c &lt; 128) { &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; utftext += String.fromCharCode(c); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; } else if((c &gt; 127) &amp;&amp; (c &lt; 2048)) { &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; utftext += String.fromCharCode((c &gt;&gt; 6) | 192); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; utftext += String.fromCharCode((c &amp; 63) | 128); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; } else { &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; utftext += String.fromCharCode((c &gt;&gt; 12) | 224); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; utftext += String.fromCharCode(((c &gt;&gt; 6) &amp; 63) | 128); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; utftext += String.fromCharCode((c &amp; 63) | 128); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; } &nbsp;<br />\r\n&nbsp; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; } &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; return utftext; &nbsp;<br />\r\n&nbsp; &nbsp; } &nbsp;<br />\r\n&nbsp; &nbsp;<br />\r\n&nbsp; &nbsp; // private method for UTF-8 decoding &nbsp;<br />\r\n&nbsp; &nbsp; _utf8_decode = function (utftext) { &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; var string = &quot;&quot;; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; var i = 0; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; var c = c1 = c2 = 0; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; while ( i &lt; utftext.length ) { &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; c = utftext.charCodeAt(i); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if (c &lt; 128) { &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; string += String.fromCharCode(c); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; i++; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; } else if((c &gt; 191) &amp;&amp; (c &lt; 224)) { &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; c2 = utftext.charCodeAt(i+1); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; string += String.fromCharCode(((c &amp; 31) &lt;&lt; 6) | (c2 &amp; 63)); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; i += 2; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; } else { &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; c2 = utftext.charCodeAt(i+1); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; c3 = utftext.charCodeAt(i+2); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; string += String.fromCharCode(((c &amp; 15) &lt;&lt; 12) | ((c2 &amp; 63) &lt;&lt; 6) | (c3 &amp; 63)); &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; i += 3; &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; } &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; } &nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; return string; &nbsp;<br />\r\n&nbsp; &nbsp; } &nbsp;<br />\r\n}</p>\r\n\r\n<p>-----------------------------------------------php 文件</p>\r\n\r\n<p>&lt;?php<br />\r\nheader(&quot;Content-type: image/jpeg&quot;);<br />\r\n$img = base64_decode($_GET[&#39;img&#39;]);<br />\r\n$ch = curl_init();<br />\r\n//设置选项，包括URL<br />\r\ncurl_setopt($ch, CURLOPT_URL,$img );<br />\r\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);<br />\r\ncurl_setopt($ch, &nbsp;CURLOPT_FOLLOWLOCATION, 1); // 302 redirect<br />\r\ncurl_setopt($ch, CURLOPT_HEADER, 0);<br />\r\ncurl_setopt($ch, CURLOPT_IPRESOLVE, CURL_IPRESOLVE_V4 );<br />\r\ncurl_setopt($ch, CURLOPT_HTTP_VERSION, CURL_HTTP_VERSION_1_0);<br />\r\n//执行并获取HTML文档内容<br />\r\n$output = curl_exec($ch);<br />\r\n//释放curl句柄<br />\r\ncurl_close($ch);<br />\r\n//打印获得的数据<br />\r\nprint_r($output);<br />\r\nexit;<br />\r\n?&gt;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '', '2017-08-23 06:35:47', '2018-12-26 17:14:47', 104),
