(330, '', 0, '递归', NULL, '', '<p style="font-family: &quot;Microsoft Yahei&quot;, arial, Tahoma, sans-serif;">重复调用setTimeout内部代码：</p>\r\n\r\n<p style="font-family: &quot;Microsoft Yahei&quot;, arial, Tahoma, sans-serif;">(function repaint(){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; //重绘相关代码<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; setTimeout(repaint，16) ;<br />\r\n&nbsp; &nbsp; })() ;<br />\r\n或者</p>\r\n\r\n<p style="font-family: &quot;Microsoft Yahei&quot;, arial, Tahoma, sans-serif;">&nbsp; &nbsp; (function repaint(){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; //重绘相关代码<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; requestAnimationFrame(repaint) ;<br />\r\n&nbsp; &nbsp; })() ;</p>\r\n', '', '2017-12-17 11:30:53', '2018-12-27 08:43:36', 59),
