(337, '', 0, 'JQ操作DOM', NULL, '', '<p style="line-height:16px;">文档：<a href="http://jquery.cuishifeng.cn/" target="_blank">http://jquery.cuishifeng.cn/</a></p>\r\n\r\n<p style="line-height:16px;">&nbsp;</p>\r\n\r\n<hr />\r\n<p style="line-height:16px;">&nbsp;</p>\r\n\r\n<p style="line-height:16px;">选择器参考：</p>\r\n\r\n<p style="line-height:16px;">-----------------------</p>\r\n\r\n<p style="line-height:16px;"><span style="font-size:12px;">-$(&quot;div,span,p.myClass&quot;)<br />\r\n-$(&quot;label + input&quot;) //匹配所有label的next元素input</span></p>\r\n\r\n<p style="line-height:16px;"><span style="font-size:12px;">-$(&quot;form ~ input&quot;) //匹配form的所有siblings元素input</span></p>\r\n\r\n<p style="line-height:16px;"><span style="font-size:12px;">-$(&quot;input:not(:checked)&quot;)</span></p>\r\n\r\n<p style="line-height:16px;"><span style="font-size:12px;">-$(&quot;td:parent&quot;) //匹配含有子元素或者文本的元素td</span></p>\r\n\r\n<p style="line-height:16px;"><span style="font-size:12px;">-$(&quot;tr:hidden&quot;) &amp; $(&quot;tr:visible&quot;)</span></p>\r\n\r\n<p style="line-height:16px;"><span style="font-size:12px;">-$(&quot;input[id][name$=&#39;man&#39;]&quot;) //复合属性选择器,同时又id属性和name属性等于&#39;man&#39;的input</span></p>\r\n\r\n<p style="line-height:16px;"><span style="font-size:12px;">-$(&#39;li:first&#39;); //获取第一个元素</span></p>\r\n\r\n<p style="line-height:16px;"><span style="font-size:12px;">-$(&quot;ul li:first-child&quot;) //匹配第一个子元素</span></p>\r\n\r\n<p style="line-height:16px;"><span style="font-size:12px;">-$(&quot;ul li:nth-child(2)&quot;) //在每个 ul 查找第 2 个li (从1开始)</span></p>\r\n\r\n<p style="line-height:16px;"><span style="font-size:12px;">-$(&quot;ul li:eq(2)&quot;) //第一个ul找第2个li?(从0开始)</span></p>\r\n\r\n<p style="line-height:16px;"><span style="font-size:12px;">-$(&quot;&lt;div&gt;&lt;p&gt;Hello&lt;/p&gt;&lt;/div&gt;&quot;)</span></p>\r\n\r\n<p style="line-height:16px;"><span style="font-size:12px;">-$(&quot;img&quot;).get(0);</span></p>\r\n\r\n<p style="line-height:16px;"><span style="font-size:12px;">-$(&#39;li&#39;).index(document.getElementById(&#39;bar&#39;)); //1，传递一个DOM对象，返回这个对象在原先集合中的索引位置</span></p>\r\n\r\n<p style="line-height:16px;"><span style="font-size:12px;">-$(&#39;li&#39;).index($(&#39;#bar&#39;)); //1，传递一个jQuery对象</span></p>\r\n\r\n<p style="line-height:16px;"><span style="font-size:12px;">-$(&#39;li&#39;).index($(&#39;li:gt(0)&#39;)); //1，传递一组jQuery对象，返回这个对象中第一个元素在原先集合中的索引位置</span></p>\r\n\r\n<p style="line-height:16px;"><span style="font-size:12px;">-$(&#39;#bar&#39;).index(&#39;li&#39;); //1，传递一个选择器，返回#bar在所有li中的索引位置</span></p>\r\n\r\n<p style="line-height:16px;"><span style="font-size:12px;">-$(&#39;#bar&#39;).index(); //1，不传递参数，返回这个元素在同辈中的索引位置。</span></p>\r\n\r\n<p style="line-height:16px;"><span style="font-size:12px;">-$.grep( [0,1,2], function(n,i){ return n &gt; 0;});//过滤数组中小于 0 的元素</span></p>\r\n\r\n<p style="line-height:16px;"><span style="font-size:12px;">-$(&quot;p&quot;).contents().not(&quot;[nodeType=1]&quot;).wrap(&quot;&lt;b/&gt;&quot;);</span></p>\r\n\r\n<p style="line-height:16px;"><span style="font-size:12px;">-$(&quot;iframe&quot;).contents().find(&quot;body&quot;) .append(&quot;I&#39;m in an iframe!&quot;);</span></p>\r\n\r\n<p style="line-height:16px;">&nbsp;</p>\r\n\r\n<hr />\r\n<p style="line-height:16px;">&nbsp;</p>\r\n\r\n<p style="line-height:16px;">方法参考：</p>\r\n\r\n<p style="line-height:16px;">-----------------------</p>\r\n\r\n<p style="line-height:16px;">&nbsp;</p>\r\n\r\n<p style="line-height:16px;"><span style="font-size:12px;">-detach():从DOM中删除所有匹配的元素。这个方法不会把匹配的元素从jQuery对象中删除，因而可以在将来再使用这些匹配的元素。与remove()不同的是，所有绑定的事件、附加的数据等都会保留下来。</span></p>\r\n\r\n<p style="line-height:16px;">&nbsp;</p>\r\n\r\n<p>获取元素名：prop(&quot;tagName&quot;)</p>\r\n\r\n<div>\r\n<p>&nbsp;</p>\r\n\r\n<p>获取屏幕高度用$(window).height();【页面必需包含&lt;!DOCTYPE HTML&gt;&lt;html&gt;...&lt;/html&gt;】</p>\r\n</div>\r\n', '', '2017-12-17 12:28:22', '2018-12-27 06:22:19', 57),
(338, '', 0, 'cookie', NULL, '', '<p>问题：<br />\r\n- Domain以及path属性？<br />\r\n- 清空要对应cookie的Domain以及path怎么写？</p>\r\n\r\n<hr />\r\n<p>&nbsp;</p>\r\n\r\n<div><span style="background-color: rgb(250, 235, 215);">// 设置cookie</span></div>\r\n\r\n<div><span style="background-color: rgb(250, 235, 215);">function setCookie(cname,cvalue,exdays){</span></div>\r\n\r\n<div><span style="background-color: rgb(250, 235, 215);">&nbsp; &nbsp; var d = new Date();</span></div>\r\n\r\n<div><span style="background-color: rgb(250, 235, 215);">&nbsp; &nbsp; d.setTime(d.getTime()+(exdays*24*60*60*1000));</span></div>\r\n\r\n<div><span style="background-color: rgb(250, 235, 215);">&nbsp; &nbsp; var expires = &quot;expires=&quot;+d.toGMTString();</span></div>\r\n\r\n<div><span style="background-color: rgb(250, 235, 215);">&nbsp; &nbsp; document.cookie = cname+&quot;=&quot;+cvalue+&quot;; &quot;+expires;</span></div>\r\n\r\n<div><span style="background-color: rgb(250, 235, 215);">}</span></div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div><span style="background-color: rgb(250, 235, 215);">// 获取cookie</span></div>\r\n\r\n<div><span style="background-color: rgb(250, 235, 215);">function getCookie(cname){</span></div>\r\n\r\n<div><span style="background-color: rgb(250, 235, 215);">&nbsp; &nbsp; var name = cname + &quot;=&quot;;</span></div>\r\n\r\n<div><span style="background-color: rgb(250, 235, 215);">&nbsp; &nbsp; var ca = document.cookie.split(&#39;;&#39;);</span></div>\r\n\r\n<div><span style="background-color: rgb(250, 235, 215);">&nbsp; &nbsp; for(var i=0; i&lt;ca.length; i++) {</span></div>\r\n\r\n<div><span style="background-color: rgb(250, 235, 215);">&nbsp; &nbsp; &nbsp; &nbsp; var c = ca[i].trim();</span></div>\r\n\r\n<div><span style="background-color: rgb(250, 235, 215);">&nbsp; &nbsp; &nbsp; &nbsp; if (c.indexOf(name)==0) { return c.substring(name.length,c.length); }</span></div>\r\n\r\n<div><span style="background-color: rgb(250, 235, 215);">&nbsp; &nbsp; }</span></div>\r\n\r\n<div><span style="background-color: rgb(250, 235, 215);">&nbsp; &nbsp; return &quot;&quot;;</span></div>\r\n\r\n<div><span style="background-color: rgb(250, 235, 215);">}</span></div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>\r\n<div><span style="background-color: rgb(250, 235, 215);">// 清空cookie</span></div>\r\n\r\n<div><span style="background-color: rgb(250, 235, 215);">function clearCookie(){&nbsp;</span></div>\r\n\r\n<div><span style="background-color: rgb(250, 235, 215);">&nbsp; &nbsp;&nbsp;var keys=document.cookie.match(/[^ =;]+(?==)/g);&nbsp;</span></div>\r\n\r\n<div><span style="background-color: rgb(250, 235, 215);">&nbsp; &nbsp;&nbsp;if (keys) {&nbsp;</span></div>\r\n\r\n<div><span style="background-color: rgb(250, 235, 215);">&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;for (var i = keys.length; i--;) {</span></div>\r\n\r\n<div><span style="background-color: rgb(250, 235, 215);">&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;document.cookie=keys[i]+&#39;=0;expires=&#39; + new Date( 0).toUTCString()&nbsp;</span></div>\r\n\r\n<div><span style="background-color: rgb(250, 235, 215);">&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;}</span></div>\r\n\r\n<div><span style="background-color: rgb(250, 235, 215);">&nbsp; &nbsp;&nbsp;}&nbsp;</span></div>\r\n\r\n<div><span style="background-color: rgb(250, 235, 215);">}&nbsp;</span></div>\r\n</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<p>设置：<br />\r\ndocument.cookie = &quot;key=value; expires=xxx&quot;;&nbsp;<br />\r\n// expires为过期时间，值如Thu, 18 Dec 2043 12:00:00 GMT<br />\r\n// 设置expires可参考上面方法setCookie</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>读取：<br />\r\nvar x = document.cookie; // 返回字符串的值类似于：a1=111;a2=222<br />\r\n// 获取某个参数的值需要对上面返回值做字符串处理，如上方法getCookie(key)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>修改：<br />\r\ndocument.cookie = &quot;key=value2; expires=xxx&quot;;&nbsp;<br />\r\n// 跟创建一样，它会把value2覆盖之前key的值</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>删除：<br />\r\ndocument.cookie = &quot;key=; expires=yyy&quot;; // 把expires设置成过去的时间就好<br />\r\n// 删除时不必指定 cookie 的值</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>-------------------------------------------------------------<br />\r\n- cookie存储数据比较少，只有几k</p>\r\n\r\n<p style="font-family: &quot;Microsoft Yahei&quot;, arial, Tahoma, sans-serif;">&nbsp;</p>\r\n\r\n<hr />\r\n<p style="font-family: &quot;Microsoft Yahei&quot;, arial, Tahoma, sans-serif;">&nbsp;</p>\r\n\r\n<p style="font-family: &quot;Microsoft Yahei&quot;, arial, Tahoma, sans-serif;">设置：$.cookie(&#39;test&#39;,&#39;123&#39;);</p>\r\n\r\n<p style="font-family: &quot;Microsoft Yahei&quot;, arial, Tahoma, sans-serif;">获取：$.cookie(&#39;test&#39;);</p>\r\n\r\n<p style="font-family: &quot;Microsoft Yahei&quot;, arial, Tahoma, sans-serif;">清空：$.cookie(&#39;test&#39;,&#39;&#39;);</p>\r\n\r\n<p style="font-family: &quot;Microsoft Yahei&quot;, arial, Tahoma, sans-serif;">php获取：$_COOKIE(&#39;test&#39;);</p>\r\n', '', '2017-12-17 12:36:24', '2018-12-27 08:44:55', 59),
(339, '', 0, 'promise', NULL, '', '<p>文档：<a href="https://developer.mozilla.org/zh-CN/docs/Web/JavaScript/Reference/Global_Objects/Promise">https://developer.mozilla.org/zh-CN/docs/Web/JavaScript/Reference/Global_Objects/Promise</a></p>\r\n\r\n<p>promise理解，</p>\r\n\r\n<p><a href="https://www.cnblogs.com/whybxy/p/7645578.html" target="_blank">https://www.cnblogs.com/whybxy/p/7645578.html</a></p>\r\n\r\n<p><a href="https://www.liaoxuefeng.com/wiki/001434446689867b27157e896e74d51a89c25cc8b43bdb3000/0014345008539155e93fc16046d4bb7854943814c4f9dc2000" target="_blank">https://www.liaoxuefeng.com/wiki/001434446689867b27157e896e74d51a89c25cc8b43bdb3000/0014345008539155e93fc16046d4bb7854943814c4f9dc2000</a></p>\r\n\r\n<p>测试示例<a href="http://jsbin.com/rukefimu/3/edit" target="_blank">http://jsbin.com/rukefimu/3/edit</a></p>\r\n\r\n<p>例子：<a href="http://www.jianshu.com/p/bb5d4db642ba" target="_blank">http://www.jianshu.com/p/bb5d4db642ba</a></p>\r\n\r\n<p>详情：<a href="https://developer.mozilla.org/zh-CN/docs/Web/JavaScript/Reference/Global_Objects/Promise" target="_blank">https://developer.mozilla.org/zh-CN/docs/Web/JavaScript/Reference/Global_Objects/Promise</a></p>\r\n\r\n<p>高级：<a href="http://www.jianshu.com/p/473cd754311f" target="_blank">http://www.jianshu.com/p/473cd754311f</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>用于管理与异步API交互的抽象对象，避免使用回调函数的层层嵌套</p>\r\n\r\n<p>状态:等待-&gt;完成-&gt;拒绝<br />\r\n必须有一个then(),第一个参数是resolved,第二个是rejected函数;</p>\r\n\r\n<p>方法：</p>\r\n\r\n<p>Promise.all(x,y) -- x,y都执行完了再往下走<br />\r\nPromise.race(x,y) -- x,y任意一个执行完了就往下走</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<hr />\r\n<p style="line-height:14px;">&nbsp;</p>\r\n\r\n<p style="line-height:14px;">\r\n<style type="text/css">p.p1 {margin: 0.0px 0.0px 0.0px 0.0px; font: 12.0px Helvetica}\r\np.p2 {margin: 0.0px 0.0px 0.0px 0.0px; font: 12.0px Helvetica; min-height: 14.0px}\r\nspan.s1 {font: 12.0px ''PingFang SC''}\r\nspan.Apple-tab-span {white-space:pre}\r\n</style>\r\n</p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">function p () {</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;return new Promise(function (resolve,reject) {</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var _p = 10;</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;console.log(&#39;ppp-&gt;&#39; + _p)</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;resolve(_p)</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;});</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">}</span></p>\r\n\r\n<p style="line-height:14px;">&nbsp;</p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">function a (p) {</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;return new Promise(function (resolve,reject) {</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;setTimeout(function () {</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var _a = p + 1;</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;console.log(&#39;aaa-&gt;&#39; + _a)</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;resolve(_a);</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;},1000);</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;});</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">}</span></p>\r\n\r\n<p style="line-height:14px;">&nbsp;</p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">function b (a) {</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;return new Promise(function (resolve,reject) {</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;setTimeout(function () {</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var _b = a + 1;</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;console.log(&#39;bbb-&gt;&#39; + _b)</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;resolve(_b)</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;},1000);</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;});</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">}</span></p>\r\n\r\n<p style="line-height:14px;">&nbsp;</p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">function c (b) {</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;return new Promise(function (resolve,reject) {</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;setTimeout(function () {</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if (b &lt;= 10) {</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var _c = b + 1</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;resolve(&#39;结果为&#39;+_c)</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;} else {</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;reject(&#39;结果大于10&#39;)</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}, 1000)</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;})</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">}</span></p>\r\n\r\n<p style="line-height:14px;">&nbsp;</p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">p()</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">.then(function (data) {</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;return a(data);</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">})</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">.then(function (data) {</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;return b(data);</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">})</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">.then(function (data) {</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;return c(data)</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">})</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">// .then(</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">// &nbsp;&nbsp; &nbsp;function (data) {</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">// &nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;console.log(&#39;resolve-&gt;&#39; + data);</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">// &nbsp;&nbsp; &nbsp;},</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">// &nbsp;&nbsp; &nbsp;function (reason) {</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">// &nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;console.log(&#39;reject-&gt;&#39; + reason);</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">// &nbsp;&nbsp; &nbsp;}</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">// );</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">.then(function (data) {</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;console.log(&#39;resolve-&gt;&#39; + data);</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">})</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">.catch(function (reason) {</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp;&nbsp; &nbsp;console.log(&#39;reject-&gt;&#39; + reason);</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">})</span></p>\r\n\r\n<p style="line-height:14px;">&nbsp;</p>\r\n\r\n<hr />\r\n<p>&nbsp;</p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">Promise</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;"><span style="color:#FF0000;">.all</span>([p(), a(), b()])</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">.then(function(results){</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">console.log(&#39;三个Promise实例都已完成，结果为：&#39;)</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp; &nbsp; console.log(results);</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">});</span></p>\r\n\r\n<p style="line-height:14px;">&nbsp;</p>\r\n\r\n<div>\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">Promise</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;"><span style="color:#FF0000;">.race</span>([p(), a(), b()])</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">.then(function(results){</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">console.log(&#39;三个Promise实例中有一个先完成， 第一个完成的是：&#39;)</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">&nbsp; &nbsp; console.log(results);</span></p>\r\n\r\n<p style="line-height:14px;"><span style="background-color:#FAEBD7;">});</span></p>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>\r\n<hr />\r\n<p>&nbsp;</p>\r\n</div>\r\n</div>\r\n', '', '2017-12-17 12:45:05', '2019-07-21 07:38:40', 64),
