(594, '', 0, 'vue.config.js', NULL, '', '<div>configureWebpack: {</div>\r\n\r\n<div style="margin-left: 40px;">plugins: [</div>\r\n\r\n<div style="margin-left: 80px;">new.webpack.ProvidePlugin({</div>\r\n\r\n<div style="margin-left: 120px;">Axios: &#39;axios&#39;</div>\r\n\r\n<div style="margin-left: 80px;">})</div>\r\n\r\n<div style="margin-left: 40px;">]</div>\r\n\r\n<div>}</div>\r\n\r\n<div>// 相当于在全局添加命名空间，即window.Axios，组件里调用时用Axios就好</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>\r\n<hr />\r\n<p>&nbsp;</p>\r\n</div>\r\n\r\n<div>transpileDependencies: [<br />\r\n&nbsp; &nbsp; &#39;vue-echarts&#39;<br />\r\n]<br />\r\n// 相当于Vue.use(vue-echarts组件)，组件含有install方法</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>\r\n<hr />\r\n<p>&nbsp;</p>\r\n\r\n<p>devServer: {<br />\r\n&nbsp; &nbsp; port: 8088,<br />\r\n&nbsp; &nbsp; host: &#39;0.0.0.0&#39;,<br />\r\n&nbsp; &nbsp; https: false,<br />\r\n&nbsp; &nbsp; open: true,<br />\r\n&nbsp; &nbsp; proxy: proxy<br />\r\n},<br />\r\ncss: {<br />\r\n&nbsp; &nbsp; loaderOptions: {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; sass: {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; data: &#39;@import &quot;./src/theme/index.scss&quot;;&#39;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; }<br />\r\n&nbsp; &nbsp; }<br />\r\n},</p>\r\n</div>\r\n', '', '2019-08-16 08:53:36', '2019-08-17 03:03:11', 118),
