(9511, 0, '', '', NULL, NULL, NULL, '', '', '', '', '', '<p>------------------------------表格样式----------------</p><p>::v-deep .table-pagination {</p><p>&nbsp; &nbsp; &amp;-head {</p><p>&nbsp; &nbsp; &nbsp; &nbsp; height: 48px;</p><p>&nbsp; &nbsp; }</p><p>&nbsp; &nbsp; &amp;-content {</p><p>&nbsp; &nbsp; &nbsp; &nbsp; height: calc(100% - 48px);</p><p>&nbsp; &nbsp; &nbsp; &nbsp; overflow-y: auto; // 默认已有overflow: auto;</p><p>&nbsp; &nbsp; }</p><p>}</p><p>::v-deep .el-table {</p><p>&nbsp; &nbsp;&nbsp;height: 100%;</p><p>&nbsp; &nbsp;&nbsp;.el-table__body-wrapper {</p><p>&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; height: calc(100% - 49px);</p><p>&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; overflow-y: auto; // 默认已有overflow: auto;</p><p>&nbsp; &nbsp;&nbsp;}</p><p>}</p>', '', '2019-10-18 11:14:38', '2019-10-18 11:14:38', 875, 0),
(9512, 0, '', '', NULL, NULL, NULL, '', '', '', '', '', '<p>------------------------------获取列表传参----------------</p><p>let params = {</p><p>&nbsp; &nbsp; PageRecordNum: this.pageSize,</p><p>&nbsp; &nbsp; RecordStartNo: (this.pageNum - 1) * this.pageSize + 1 || 1,</p><p>&nbsp; &nbsp; Keyword: this.keyword || &#39;&#39;</p><p>};</p>', '', '2019-10-18 11:14:48', '2019-10-18 11:14:48', 875, 0),
(9513, 0, '', '', NULL, NULL, NULL, '', '', '', '', '', '<p>------------------------------节流----------------</p><p>data () { debounce: new this.$utils.DebounceTime(500) }</p><p>created () {</p><p>&nbsp; &nbsp; this.debounce.subscribe((value) =&gt; {</p><p>&nbsp; &nbsp; &nbsp; &nbsp; this.$emit(&#39;commit&#39;, value);</p><p>&nbsp; &nbsp; });</p><p>},</p><p>methods: {</p><p>&nbsp; &nbsp;&nbsp;confirm () { let rs = &#39;xxx&#39;; this.debounce.next(rs); }</p><p>}</p><p>destoryed () {</p><p>&nbsp; &nbsp; this.debounce.unsubscribe();</p><p>}</p>', '', '2019-10-18 11:14:59', '2019-10-18 11:14:59', 875, 0),
(9514, 0, '', '', NULL, NULL, NULL, '', '', '', '', '', '<p>------------------------------操作回调----------------</p><p>增加 -&gt; pageNum = 1; this.queryTableList(); // 第一页刷新</p><p>修改 -&gt; this.$set(this.tableList, this.currentIndex, returnData); // 当前条目更新</p><p>删除 -&gt; this.queryTableList(); // 当前页刷新</p>', '', '2019-10-18 11:15:07', '2019-10-18 11:15:07', 875, 0),
