(9598, 0, '', '', NULL, NULL, NULL, '', '', '', '', '', '<p>// 过滤出目录树有勾选的部分</p><p>checkedFilter () {</p><p>&nbsp; let nodes = this.$refs.tableLt.getCheckedNodes().concat(this.$refs.tableLt.getHalfCheckedNodes());</p><p>&nbsp; let nodesCopy = _.cloneDeep(nodes);</p><p>&nbsp; nodesCopy.map(x =&gt; {</p><p>&nbsp; &nbsp; if (x.Children !== undefined &amp;&amp; x.Children.length &gt; 0) {</p><p>&nbsp; &nbsp; &nbsp; let xChildrenChecked = [];</p><p>&nbsp; &nbsp; &nbsp; x.Children.map(y =&gt; {</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; let index = nodes.findIndex(z =&gt; z.Id === y.Id);</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if (index &gt;= 0) {</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; xChildrenChecked.push(y);</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; nodes.splice(index, 1); // 删掉最外层与Children重复的</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p><p>&nbsp; &nbsp; &nbsp; });</p><p>&nbsp; &nbsp; &nbsp; let index2 = nodes.findIndex(z =&gt; z.Id === x.Id);</p><p>&nbsp; &nbsp; &nbsp; nodes[index2].Children = xChildrenChecked;</p><p>&nbsp; &nbsp; }</p><p>&nbsp; });</p><p>&nbsp; return nodes;</p><p>}</p><p data-f-id="pbf" style="text-align: center; font-size: 14px; margin-top: 30px; opacity: 0.65; font-family: sans-serif;">Powered by <a href="https://www.froala.com/wysiwyg-editor?pb=1" title="Froala Editor">Froala Editor</a></p>', '', '2019-10-30 21:17:38', '2019-10-30 21:17:38', 522, 0),
(9599, 0, '', '', NULL, NULL, NULL, '', '', '', '', '', '<p>// 比对已选项回显</p><p>checkedDisplay () {</p><p>&nbsp; let checkedKeys = [];</p><p>&nbsp; let flatten = (list) =&gt; {</p><p>&nbsp; &nbsp; list.map(x =&gt; {</p><p>&nbsp; &nbsp; &nbsp; &nbsp; if (!x.Children || x.Children.length &lt;= 0) {</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; checkedKeys.push(x.Id);</p><p>&nbsp; &nbsp; &nbsp; &nbsp; } else {</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; let children = x.Children;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; flatten(children);</p><p>&nbsp; &nbsp; &nbsp; &nbsp; }</p><p>&nbsp; &nbsp; });</p><p>&nbsp; }</p><p>&nbsp; flatten(this.data.Children);</p><p>&nbsp; this.$refs.tableLt.setCheckedKeys(checkedKeys);</p><p>}</p><p data-f-id="pbf" style="text-align: center; font-size: 14px; margin-top: 30px; opacity: 0.65; font-family: sans-serif;">Powered by <a href="https://www.froala.com/wysiwyg-editor?pb=1" title="Froala Editor">Froala Editor</a></p>', '', '2019-10-30 21:18:03', '2019-10-30 21:18:03', 522, 0);
