(341, 0, '', 0, '<p>中台架构思考:</p>\r\n\r\n<p>-----------------------------------------------------</p>\r\n\r\n<p>main.js:<br />\r\n<span style="color:#0000FF;">import &#39;./plugins.js&#39;;</span><br />\r\nnew Vue({...}).$mount(&#39;#app&#39;);</p>\r\n\r\n<p><span style="color:#0000FF;">plugins.js:</span><br />\r\nimport <span style="color:#FF0000;">elementUI </span>from &#39;element-ui&#39;;<br />\r\nimport <span style="color:#FF0000;">ECharts</span> from &#39;vue-echarts/components/ECharts&#39;;</p>\r\n\r\n<p>Vue.use(elementUI);<br />\r\nVue.component(&#39;v-chart&#39;, ECharts);</p>\r\n\r\n<p>-----------------------------------------------------</p>\r\n\r\n<p>UUID&nbsp; 通用单一标识符 (Universally Unique Identifier)<br />\r\nGUID&nbsp; 全局唯一标识符 (Globally Unique Identifier)</p>\r\n\r\n<p>-----------------------------------------------------</p>\r\n\r\n<p>class service {</p>\r\n\r\n<p style="margin-left: 40px;">constructor () {</p>\r\n\r\n<p style="margin-left: 80px;">this.subject =<span style="color:#FF0000;">new Subject()</span>;</p>\r\n\r\n<p style="margin-left: 40px;">}</p>\r\n\r\n<p style="margin-left: 40px;">send (data) {</p>\r\n\r\n<p style="margin-left: 80px;">this.subject.next(data);</p>\r\n\r\n<p style="margin-left: 40px;">}</p>\r\n\r\n<p style="margin-left: 40px;">get () { // subscribe</p>\r\n\r\n<p style="margin-left: 80px;">return this.subject.<span style="color:#FF0000;">asObservable</span>();</p>\r\n\r\n<p style="margin-left: 40px;">}</p>\r\n\r\n<p>}</p>\r\n\r\n<p>export const xxx = new service();</p>\r\n', '2019-07-16 01:29:33', '2019-07-21 06:17:27', 4, 0),
(342, 0, '', 0, '<p>需要理解：</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>WebSocket</p>\r\n\r\n<p>https</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>bing搜索？</p>\r\n\r\n<p>翻墙</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>分布式</p>\r\n\r\n<p>微服务</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>并发 &amp; 并行</p>\r\n\r\n<p>多任务 &amp; 多线程 &amp; 多进程</p>\r\n\r\n<p>高负载，负载均衡</p>\r\n\r\n<p style="line-height:14px;">镜像</p>\r\n\r\n<p style="line-height:14px;">&nbsp;</p>\r\n\r\n<p style="line-height:14px;">vM虚拟机</p>\r\n\r\n<p style="line-height:14px;">Linux</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>服务端渲染</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>I/O</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style="font-family: arial, 宋体, sans-serif; font-size: 14px; text-indent: 28px; background-color: rgb(255, 255, 255);">Apache、IIS</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Serverless</p>\r\n\r\n<p>IaaS及Pass</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>宏观和微观</p>\r\n\r\n<p>世界是全息的</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>---------------------------------------------------------</p>\r\n\r\n<p><span style="font-family: arial, 宋体, sans-serif; font-size: 14px; text-indent: 28px; background-color: rgb(255, 255, 255);">html静态化也是某些缓存策略使用的手段，对于系统中频繁使用数据库查询但是内容更新很小的应用，可以考虑使用html静态化来实现，比如论坛中论坛的公用设置信息，这些信息目前的主流论坛都可以进行后台管理并且存储再数据库中，这些信息其实大量被前台程序调用，但是更新频率很小，可以考虑将这部分内容进行<span style="color:#FF0000;">后台更新的时候进行静态化</span>，这样避免了大量的数据库访问请求。</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '2019-07-21 01:41:24', '2019-07-21 11:40:50', 4, 0),
