(173, '', 0, 'ajax基础', NULL, '', '<p>参数：http://blog.sina.com.cn/s/blog_4f925fc30100la36.html</p>\r\n\r\n<p>http://www.w3school.com.cn/jquery/ajax_ajax.asp</p>\r\n\r\n<hr />\r\n<p>test:</p>\r\n\r\n<p>//IE5和IE6中模拟XMLHttpRequest()构造函数：<br />\r\nif(window.XMLHttpRequest===undefined){<br />\r\n&nbsp; window.XMLHttpRequest=function(){<br />\r\n&nbsp; try{<br />\r\n&nbsp; &nbsp; //如果可用，则用ActiveX对象的最新版本<br />\r\n&nbsp; &nbsp; return new ActiveXObject(&#39;Msxml2.XMLHTTP.6.0&#39;); &nbsp;<br />\r\n&nbsp; }catch(e1){<br />\r\n&nbsp; &nbsp; try{<br />\r\n&nbsp; &nbsp; &nbsp; //否则，回退到较旧的版本<br />\r\n&nbsp; &nbsp; &nbsp; return new ActiveXObject(&#39;Msxml2.XMLHTTP.3.0&#39;);&nbsp;<br />\r\n&nbsp; &nbsp; }catch(e2){<br />\r\n&nbsp; &nbsp; &nbsp; throw new Error(&#39;XMLHttpRequest is not supported&#39;);<br />\r\n&nbsp; &nbsp; }<br />\r\n&nbsp; }<br />\r\n}</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<hr />\r\n<p>&nbsp;</p>\r\n\r\n<p>实例一：<br />\r\n-----------<br />\r\nvar xhr=null;<br />\r\nif(window.XMLHttpRequest){<br />\r\n&nbsp;&nbsp; &nbsp;xhr=new XMLHttpRequest();&nbsp;&nbsp; &nbsp;<br />\r\n}else if(window.ActiveXObject){<br />\r\n&nbsp;&nbsp; &nbsp;try{<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;xhr=new ActiveXObject(&#39;Msxml2.XMLHTTP&#39;);&nbsp;&nbsp; &nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;}catch(e){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;try{<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;xhr=new ActiveXObject(&#39;Microsoft.XMLHTTP&#39;);&nbsp;&nbsp; &nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}catch(e){}<br />\r\n&nbsp;&nbsp; &nbsp;}<br />\r\n}<br />\r\nxhr.onreadystatechange=function(){<br />\r\n&nbsp; &nbsp; if (xhr.readyState==4 &amp;&amp; xhr.status==200){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;alert(xhr.responseText);<br />\r\n&nbsp; &nbsp; }<br />\r\n}<br />\r\nxhr.open(&quot;GET&quot;,&quot;ajax_test.php&quot;,true);<br />\r\nxhr.send();</p>\r\n\r\n<p>----------------------------------------------------------</p>\r\n\r\n<p>|||&gt;&gt;各浏览器实例化：<br />\r\n-----------<br />\r\nnew XMLHttpRequest(); &nbsp;__//for IE7+, Firefox, Chrome, Opera, Safari<br />\r\nnew ActiveXObject(&#39;Msxml2.XMLHTTP&#39;); __//for IE6+<br />\r\nnew ActiveXObject(&quot;Microsoft.XMLHTTP&quot;); &nbsp;__// code for &lt; IE6</p>\r\n\r\n<p>----------------------------------------------------------</p>\r\n\r\n<p>|||&gt;&gt;XMLHttpRequest对象的方法和属性：<br />\r\n-----------<br />\r\n--open()：建立到服务器的新请求，初始化基本信息。&nbsp;<br />\r\n--setRequestHeader(): 请求的头部。&nbsp;<br />\r\n--send()：向服务器发送请求。<br />\r\n--abort()：退出当前请求。&nbsp;<br />\r\n--readyState：提供当前 HTML 的就绪状态。&nbsp;<br />\r\n--responseText：服务器返回的请求响应文本。<br />\r\n-------------------------------------<br />\r\n**//open() 方法的五个参数：[例：xhr.open(&quot;GET&quot;,&quot;ajax_test.php&quot;,true);]<br />\r\n-----------<br />\r\n--request-type：发送请求的类型。典型的值是 GET 或 POST，但也可以发送 HEAD 请求。&nbsp;<br />\r\n--url：要（请求）连接的 URL。&nbsp;<br />\r\n--asynch：如果希望使用异步连接则为 true，否则为 false。该参数是可选的，默认为 true。&nbsp;<br />\r\n--username：如果需要身份验证，则可以在此指定用户名。该可选参数没有默认值。&nbsp;<br />\r\n--password：如果需要身份验证，则可以在此指定口令。该可选参数没有默认值。&nbsp;<br />\r\n-------------------------------------<br />\r\n**//setRequestHeader()请求的头部：<br />\r\n-----------<br />\r\n--可多次调用<br />\r\n--如：xhr.setRequestHeader(&quot;Content-Type&quot;,&quot;application/json&quot;); //指定数据类型<br />\r\n--如：xhr.setRequestHeader(&quot;name&quot;,&quot;vale&quot;); //自定义请求头的名称<br />\r\n-------------------------------------<br />\r\n**//send()发送请求:<br />\r\n-----------<br />\r\n--如果用POST请求类型，可把传输的数据放send内<br />\r\n--如: xhr.send(&quot;kw=a&amp;pass=b&quot;);<br />\r\n--如果是json格式：(如不知道发送json数据的浏览器需要另外加载&#39;json.js&#39;)<br />\r\n&nbsp; xhr.send(JSON.stringify({<br />\r\n&nbsp; &nbsp;&nbsp; &nbsp;kw:&#39;a&#39;,<br />\r\n&nbsp; &nbsp; pass:&#39;b&#39;<br />\r\n&nbsp; }));</p>\r\n\r\n<p style="line-height:16px;">&nbsp;</p>\r\n\r\n<p style="line-height:16px;"><span style="font-size:12px;"><span style="background-color:#F0FFF0;">function joinParam(a) {//</span></span><span style="background-color:#F0FFF0;">对象属性用&#39;&amp;&#39;连成字符串:</span><br />\r\n<span style="font-size:12px;"><span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; var s = [];</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; if (a.constructor == Array) {// 如果参数是数组</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; for (var i in a) {</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; s.push(encodeURIComponent(a[i].name) + &quot;=&quot; + encodeURIComponent(a[i].value));</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; } else {</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; for (var i in a) {//其他对象</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; var ai = a[i];</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if (ai &amp;&amp; ai.constructor == Array) {</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; for (var j in ai) {</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; s.push(encodeURIComponent(i) + &quot;=&quot; + encodeURIComponent(ai[j]));</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; } else {</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; s.push(encodeURIComponent(i) + &quot;=&quot; + encodeURIComponent(ai));</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; return s.join(&quot;&amp;&quot;).replace(/%20/g, &quot;+&quot;);</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; &nbsp; &nbsp; }</span><br />\r\n<span style="background-color:#F0FFF0;">&nbsp; }</span></span></p>\r\n\r\n<p><br />\r\n-------------------------------------<br />\r\n**//HTTP五种就绪状态_[request.readyState]：<br />\r\n-----------<br />\r\n--0：未初始化&mdash;&mdash;&mdash;&mdash;[请求没有发出（在调用 open() 之前）]&nbsp;<br />\r\n--1：连接建立，请求发送&mdash;&mdash;&mdash;&mdash;[请求已经建立但还没有发出（调用 send() 之前）] &nbsp;<br />\r\n--2：服务器返回响应&mdash;&mdash;&mdash;&mdash;[请求已经发出正在处理之中（这里通常可以从响应得到内容头部）] &nbsp;<br />\r\n--3：处理响应数据&mdash;&mdash;&mdash;&mdash;[请求已经处理，响应中通常有部分数据可用，但是服务器还没有完成响应] &nbsp;<br />\r\n--4：完成，数据可交付客户端使用&mdash;&mdash;&mdash;&mdash;[响应已完成，可以访问服务器响应并使用它]&nbsp;<br />\r\n-------------------------------------<br />\r\n**//几种状态代码_[request.status]：（还有更多）<br />\r\n-----------<br />\r\n--500：服务器后台程序处理错误<br />\r\n--401：未经授权<br />\r\n--403：禁止<br />\r\n--404：没找到<br />\r\n--200：一切正常</p>\r\n\r\n<p>----------------------------------------------------------</p>\r\n\r\n<p>|||&gt;&gt;就绪后的几种状态测试：<br />\r\n-----------<br />\r\nfunction updatePage() {<br />\r\n&nbsp; &nbsp; if (request.readyState == 4) {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; if (request.status == 200) {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; alert(&quot;服务器已完成！&quot;);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; } else if (request.status == 404) {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; alert (&quot;请求URL不存在！&quot;);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; } else if (request.status == 403) {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; alert(&quot;禁止访问！&quot;);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; } else<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; alert(&quot;状态代码： &quot; + request.status);<br />\r\n&nbsp; &nbsp; }<br />\r\n}</p>\r\n\r\n<p>-------------------------------------</p>\r\n\r\n<p>|||&gt;&gt;xhr.onload<br />\r\n-----------<br />\r\n如果支持level2时，可用xhr.onload代替.xhr.readyState===4,代码如下：<br />\r\nxhr.onload=function(){<br />\r\n&nbsp;&nbsp; &nbsp;if(xhr.status==200){<br />\r\n&nbsp; &nbsp; &nbsp;&nbsp; &nbsp;...<br />\r\n&nbsp; &nbsp; }<br />\r\n}</p>\r\n\r\n<p>-------------------------------------</p>\r\n\r\n<p>**//返回数据的属性：<br />\r\n-----------<br />\r\n--xhr.responseText &nbsp;//转换成json对象：JSON.parse(xhr.responseText) or eval(xhr.responseText);<br />\r\n--xhr.responseXML<br />\r\n--xhr.getAllResponseHeaders();<br />\r\n--xhr.getResponseHeader(&quot;Content-Type&quot;);<br />\r\n--xhr.status<br />\r\n--xhr.statusText</p>\r\n\r\n<p>-------------------------------------</p>\r\n\r\n<p>|||&gt;&gt;readyState &amp; status &amp; responseText 测试<br />\r\n-----------<br />\r\nfunction updatePage() {<br />\r\n&nbsp; &nbsp; alert(&quot;updatePage() called with ready state of &quot; + request.readyState +<br />\r\n&nbsp; &nbsp; &quot; and a response text of &#39;&quot; + request.responseText + &quot;&#39;&quot;);<br />\r\n}</p>\r\n\r\n<p>-------------------------------------</p>\r\n\r\n<p>|||&gt;&gt;就绪状态实例：<br />\r\n-----------<br />\r\n可以在就绪状态发生变化时更新表单或页面中的域。<br />\r\n例如，对于就绪状态 1 来说要将进度指示器的宽度设置为 25%，<br />\r\n对于就绪状态 2 来说要将进度指示器的宽度设置为 50%，<br />\r\n对于就绪状态 3 来说要将进度指示器的宽度设置为 75%，<br />\r\n当就绪状态为 4 时将进度指示器的宽度设置为 100%（完成）。</p>\r\n\r\n<p>-------------------------------------</p>\r\n\r\n<p>**//重定向（300 系列的状态代码）：<br />\r\n-----------<br />\r\n--301：永久移动<br />\r\n--302：找到（请求被重新定向到另外一个 URL/URI 上）<br />\r\n--305：使用代理（请求必须使用一个代理来访问所请求的资源）<br />\r\n结果是您的请求无法重定向到其他服务器上，而不会产生安全性错误。<br />\r\n在这些情况中，您根本就不会得到状态代码。<br />\r\n通常在调试控制台中都会产生一个 JavaScript 错误。<br />\r\n因此，在对状态代码进行充分的考虑之后，您就可以完全忽略重定向代码的问题了。<br />\r\n一旦接收到状态代码 200 并且意识到可以很大程度上忽略 300 系列的状态代码之后，<br />\r\n所需要担心的唯一一组代码就是 400 系列的代码了</p>\r\n\r\n<p>----------------------------------------------------------</p>\r\n\r\n<p>|||&gt;&gt;Async = false：<br />\r\n-----------<br />\r\nAsync = false时，不要编写 onreadystatechange 函数 - 把代码放到 send() 语句后面即可：<br />\r\nxmlhttp.open(&quot;GET&quot;,&quot;test.txt&quot;,false);<br />\r\nxmlhttp.send();<br />\r\ndocument.getElementById(&quot;myDiv&quot;).innerHTML=xmlhttp.responseText;</p>\r\n\r\n<p>----------------------------------------------------------</p>\r\n\r\n<p>|||&gt;&gt;XML:<br />\r\n-----------<br />\r\n如果来自服务器的响应是 XML，而且需要作为 XML 对象进行解析，请使用 responseXML 属性：</p>\r\n\r\n<p>&nbsp; &nbsp; |||&gt;&gt;例1:<br />\r\n&nbsp; &nbsp; -----------<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; xmlDoc=xmlhttp.responseXML;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; x=xmlDoc.getElementsByTagName(&quot;ARTIST&quot;);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; txt=&quot;&quot;;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; for (i = 0; i &lt; x.length; i++){<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; txt=txt + x[i].childNodes[0].nodeValue + &quot;&lt;br /&gt;&quot;;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; }<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; document.getElementById(&quot;myDiv&quot;).innerHTML=txt;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<br />\r\n&nbsp; &nbsp; |||&gt;&gt;例2:<br />\r\n&nbsp; &nbsp; -----------<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; var response = request.responseText.split(&quot;|&quot;);<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; document.getElementById(&quot;aaa&quot;).value = response[0];<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; document.getElementById(&quot;bbb&quot;).innerHTML = response[1].replace(/ /g, &quot;&lt;br /&gt;&quot;);<br />\r\n&nbsp; &nbsp;&nbsp;<br />\r\n----------------------------------------------------------</p>\r\n\r\n<p>|||&gt;&gt;HEAD 请求:<br />\r\n-----------<br />\r\nfunction getSalesData() {<br />\r\n&nbsp; &nbsp; &nbsp;createRequest();<br />\r\n&nbsp; &nbsp; &nbsp;var url = &quot;/boards/servlet/UpdateBoardSales&quot;;<br />\r\n&nbsp; &nbsp; &nbsp;request.open(&quot;HEAD&quot;, url, true);<br />\r\n&nbsp; &nbsp; &nbsp;request.onreadystatechange = updatePage();<br />\r\n&nbsp; &nbsp; &nbsp;request.send(null);<br />\r\n}<br />\r\nfunction updatePage() {<br />\r\n&nbsp; &nbsp; if (request.readyState == 4) {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; alert(request.getAllResponseHeaders());<br />\r\n&nbsp; &nbsp; }<br />\r\n}<br />\r\n要获取响应的长度，只需要调用 request.getResponseHeader(&quot;Content-Length&quot;);。<br />\r\n要获取内容类型，请使用 request.getResponseHeader(&quot;Content-Type&quot;);</p>\r\n\r\n<p>----------------------------------------------------------</p>\r\n\r\n<p>**//timeout:<br />\r\n-----------<br />\r\nlevel2才支持：<br />\r\nxhr.timeout=5000;<br />\r\nxhr.ontimeout=function(){<br />\r\n&nbsp;&nbsp; &nbsp;console.log(&#39;timeout!&#39;);<br />\r\n}</p>\r\n\r\n<p>----------------------------------------------------------</p>\r\n\r\n<p>|||&gt;&gt;实例二：<br />\r\n--------------------------<br />\r\nvar xmlHttp = false;<br />\r\ntry {<br />\r\n&nbsp; xmlHttp = new ActiveXObject(&quot;Msxml2.XMLHTTP&quot;);<br />\r\n} catch (e) {<br />\r\n&nbsp; try {<br />\r\n&nbsp; &nbsp; xmlHttp = new ActiveXObject(&quot;Microsoft.XMLHTTP&quot;);<br />\r\n&nbsp; } catch (e2) {<br />\r\n&nbsp; &nbsp; xmlHttp = false;<br />\r\n&nbsp; }<br />\r\n}<br />\r\nif (!xmlHttp &amp; typeof XMLHttpRequest != &#39;undefined&#39;) {<br />\r\n&nbsp; xmlHttp = new XMLHttpRequest();<br />\r\n}<br />\r\nfunction callServer() {<br />\r\n&nbsp; var city = document.getElementById(&quot;city&quot;).value;<br />\r\n&nbsp; var state = document.getElementById(&quot;state&quot;).value;<br />\r\n&nbsp; if ((city == null) || (city == &quot;&quot;)) return;<br />\r\n&nbsp; if ((state == null) || (state == &quot;&quot;)) return;<br />\r\n&nbsp; var url = &quot;/scripts/getZipCode.php?city=&quot; + escape(city) + &quot;&amp;state=&quot; + escape(state);<br />\r\n&nbsp; xmlHttp.open(&quot;POST&quot;, url, true);<br />\r\n&nbsp; xmlHttp.onreadystatechange = updatePage;<br />\r\n&nbsp; xmlHttp.send(&#39;kw=a&amp;pass=b&#39;);<br />\r\n}<br />\r\nfunction updatePage() {<br />\r\n&nbsp; if (xmlHttp.readyState == 4) {<br />\r\n&nbsp; &nbsp; var response = xmlHttp.responseText;<br />\r\n&nbsp; &nbsp; document.getElementById(&quot;zipCode&quot;).value = response;<br />\r\n&nbsp; }<br />\r\n}</p>\r\n', '2016-11-22 07:21:24', '2018-12-27 06:59:04', 64, 0, ''),
(174, '', 0, 'DOM优化', NULL, '', '<p>&nbsp; &nbsp; JS：由 &#39;DOM&#39; &amp; &#39;ECMA&#39; &amp; &#39;BOM&#39; 组成<br />\r\n&nbsp; &nbsp; ------------------------------------</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;DOM：用于操作XML和HTML文档的应用程序<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; 包括：-Dom节点<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; -Dom树<br />\r\n&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; -Dom API<br />\r\n&nbsp;&nbsp; &nbsp;------------------------------------</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;/***优化:***/<br />\r\n&nbsp;&nbsp; &nbsp;*************</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;DOM与JS独立操作：<br />\r\n&nbsp;&nbsp; &nbsp;------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;-&gt; 浏览器操作DOM与操作JS是独立实现的<br />\r\n&nbsp;&nbsp; &nbsp;-&gt; 操作DOM与JS之间的交互(切换)是会消耗性能的<br />\r\n&nbsp;&nbsp; &nbsp;*1-1 尽可能减少两者之间的频繁交互<br />\r\n&nbsp;&nbsp; &nbsp;*1-2 innerHTML与DOM方法的对比</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;减少DOM操作：<br />\r\n&nbsp;&nbsp; &nbsp;------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;*2-1 节点克隆 - cloneNode<br />\r\n&nbsp;&nbsp; &nbsp;*2-2 访问元素集合 - 尽量用局部变量<br />\r\n&nbsp;&nbsp; &nbsp;*2-3 元素节点 - 尽量用只获取元素的节点方法<br />\r\n&nbsp;&nbsp; &nbsp;*2-4 选择器API - 利用querySelector,querySelectorAll</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;减少重排&amp;重绘：<br />\r\n&nbsp;&nbsp; &nbsp;------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;-&gt; 重排：改变页面的内容<br />\r\n&nbsp;&nbsp; &nbsp;-&gt; 重绘：浏览器显示内容<br />\r\n&nbsp;&nbsp; &nbsp;*3-1 添加顺序 - 尽量在appendChild前添加操作<br />\r\n&nbsp;&nbsp; &nbsp;*3-2 合并dom操作 - 利用cssText<br />\r\n&nbsp;&nbsp; &nbsp;*3-3 缓存布局信息<br />\r\n&nbsp;&nbsp; &nbsp;*3-4 文档碎片 - createDocumentFragment();</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;其他：<br />\r\n&nbsp;&nbsp; &nbsp;------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;-&gt; Dom与事件 - 事件委托<br />\r\n&nbsp;&nbsp; &nbsp;-&gt; Dom与前端模板 - 能更好地对逻辑和视图分离，MVC架构的基础</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;测试实例：<br />\r\n&nbsp;&nbsp; &nbsp;------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;&lt;html&gt;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&lt;div id=&quot;div1&quot;&gt;&lt;/div&gt;<br />\r\n&nbsp;&nbsp; &nbsp;&lt;/html&gt;<br />\r\n&nbsp;&nbsp; &nbsp;window.onload=function(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var oDiv=document.getElementById(&quot;div1&quot;);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;console.time(&quot;hi&quot;);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&mdash;&mdash;1-1&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;for(var i=0;i &lt; 5000;i++){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oDiv.innerHTML+=&#39;&lt;li&gt;i&lt;/li&gt;&#39;; //情况一：JS循环一次就操作一次DOM<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&mdash;&mdash; &nbsp; &mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var str=&#39;&#39;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;for(var i=0;i &lt; 5000;i++){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;str+=&#39;&lt;li&gt;li&lt;/li&gt;&#39;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oDiv.innerHTML=str; //情况二：JS每次循环先存起来，再一次性操作DOM，比上一例要快<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&mdash;&mdash;1-2&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;for(var i=0;i &lt; 5000;i++){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var oLi=document.createElement(&quot;li&quot;);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oLi.innerHTML=&quot;li&quot;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oDiv.appendChild(oLi); //webkit浏览器中比用innerHTML方法快，其他浏览器相反<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;//结论：webkit类型浏览器(chrome)的DOM方法比innerHTML性能要好<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&mdash;&mdash;2-1&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var oLi=document.createElement(&quot;li&quot;);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oLi.innerHTML=&quot;li&quot;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;for(var i=0;i &lt; 5000;i++){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var newLi=oLi.cloneNode(true); //节点克隆比重新创建一个节点性能好，减少了DOM操作<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oUl.appendChild(newLi);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&mdash;&mdash;2-2&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;for(var i=0;i &lt; oLi.length; i++){ //每次循环都要重新操作DOM来获取oLi.length集合<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;aLi[i].innerHTML=&quot;li&quot;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&mdash;&mdash; &nbsp; &mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var len=oLi.length; //存储到一个变量里，比上一例要快<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;for(var i=0; i &lt; len; i++){...}<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;//同类情况：<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var doc=document;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var oLi=doc.getElementById();<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var oUl=doc.getElementById();<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var oOl=doc.getElementById();<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&mdash;&mdash;2-3&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;childNodes -&gt; 元素节点 + 文本节点<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;children -&gt; 元素节点<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;//用childNodes 代替 children 来提高性能<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;//同理用firstElementChild 代替 firstChild 来提高性能<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&mdash;&mdash;2-4&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var oUl=document.getElementById(&quot;ul&quot;);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var oLi=oUl.getElementById(&quot;li&quot;);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;//用下例的操作一次DOM来获取代替上面的要操作两次DOM来提高性能<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var aLi=document.querySelectorAll(&quot;#ul li&quot;); //ie8以下的不支持<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&mdash;&mdash;3-1&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;for(var i=0;i &lt; 5000;i++){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var oLi=document.createElement(&quot;li&quot;);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oDiv.appendChild(oLi);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oLi.innerHTML=&quot;li&quot;; //此句应放到appendChild上面从而减少重排重绘操作<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&mdash;&mdash;3-2&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oLi.style.width=&#39;100px&#39;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oLi.style.background=&#39;red&#39;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;//用下例的合并操作代替上面的分开操作来提高性能<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oLi.style.cssText=&#39;width:100px;background=red&#39;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&mdash;&mdash;3-3&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;setInterval(function(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oDiv.style.left=oDiv.offsetLeft + &quot;px&quot;; //每次都要操作一次DOM<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oDiv.style.left=oDiv.offsetTop + &quot;px&quot;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;},30);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&mdash;&mdash; &nbsp; &mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var L=oDiv.offsetLeft; &nbsp;//把布局信息缓存起来再操作DOM,比上一例性能要好<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var T=oDiv.offsetTop;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;setInterval(function(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;L++;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;T++;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oDiv.style.left= L + &quot;px&quot;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oDiv.style.left= T + &quot;px&quot;;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;},30);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&mdash;&mdash;3-4&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var oFrag=document.createDocumentFragment();<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;for(var i=0;i &lt; 5000;i++){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var oLi=document.createElement(&quot;li&quot;);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oFrag.appendChild(oLi); //把文档碎片都先存储到oFrag内<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;oDiv.appendChild(oFrag); //再一次性操作DOM，比例2-1性能好<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;console.timeEnd(&quot;hi&quot;);<br />\r\n&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;</p>\r\n', '2016-11-22 07:23:12', '2016-11-21 19:23:12', 47, 0, ''),
(175, '', 0, 'requirejs基础', NULL, '', '<p>&nbsp;&nbsp; &nbsp;用requirejs的<span style="color:#B22222;">好处</span>：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;* 实现JS文件的异步加载，避免网页被堵塞<br />\r\n&nbsp;&nbsp; &nbsp;* 管理模块之间的依赖性，便于代码的编写和维护</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="line-height:14px;">&nbsp;&nbsp; &nbsp;实例<span style="color:#B22222;">目录结构</span>：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;test</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;js</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;app</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;a.js</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;b.js</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;lib</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;jquery1.7.js</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;jquery1.9.js</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;app.js</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;index.html</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;<span style="color:#B22222;">安装</span>：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;&lt;script src=&quot;js/require.js&quot; defer async=&quot;true&quot; data-main=&quot;js/app.js&quot;&gt;&lt;/script&gt;<br />\r\n&nbsp;&nbsp; &nbsp;//Async属性表明文件需要异步加载，IE不支持这个属性，只支持defer<br />\r\n&nbsp;&nbsp; &nbsp;//data-main 这个属性来加载初始化的JS文件</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;<span style="color:#B22222;">定义</span>模块：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;//如a.js如下:<br />\r\n&nbsp;&nbsp; &nbsp;define([&#39;依赖文件1&#39;,&#39;依赖文件2&#39;],function(){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var add = function(x,y) {<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return x + y;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;};<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return {<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;add : add<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;});<br />\r\n&nbsp;&nbsp; &nbsp;//没有依赖项&#39;[]&#39;可空，也可省略</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;<span style="color:#B22222;">调用</span>：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;//如app.js如下:<br />\r\n&nbsp;&nbsp; &nbsp;require([&#39;app/a&#39;], function (a){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;console.log(a.add(1,1));<br />\r\n&nbsp;&nbsp; &nbsp;});<br />\r\n&nbsp;&nbsp; &nbsp;______________________________________________</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;//带错误回调：<br />\r\n&nbsp;&nbsp; &nbsp;require([&#39;app/a&#39;],function(a){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;alert(a);<br />\r\n&nbsp;&nbsp; &nbsp;},function(err){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;alert(err);<br />\r\n&nbsp;&nbsp; &nbsp;});<br />\r\n&nbsp;&nbsp; &nbsp;//载入错误时，不能执行第一个回调，转而执行第二个回调<br />\r\n&nbsp;&nbsp; &nbsp;______________________________________________</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;//载入失败回调中可以使用undef函数移除模块的注册<br />\r\n&nbsp;&nbsp; &nbsp;require([&#39;b&#39;],function($){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;//Do something with $ here<br />\r\n&nbsp;&nbsp; &nbsp;},function(err){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var failedId=err.requireModules &amp;&amp; &nbsp;err.requireModules[0]; //获得模块名<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;if(failedId===&#39;b&#39;){<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;requirejs.undef(failedId);<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br />\r\n&nbsp;&nbsp; &nbsp;})</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;/***<strong><span style="color:#B22222;">配置项:</span></strong>***/<br />\r\n&nbsp;&nbsp; &nbsp;*************</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;<span style="color:#800080;">baseUrl</span>：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;//基准目录 （没配置时由data-main指定）</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;<span style="color:#800080;">paths</span>：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;//映射不直接放在baseUrl下的文件目录，起始位置相对于baseUrl</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;<span style="color:#800080;">shim</span>：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;//为没有使用define定义的模块来声明依赖关系</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;<span style="color:#800080;">map</span>：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;//解决同一模块不同版本的问题</p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;<span style="color:#800080;">config</span>：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;//将配置信息传给一个模块</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="line-height:14px;">&nbsp;&nbsp; &nbsp;实例：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;requierjs.config({</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;baseUrl:&#39;js/app&#39;,</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;paths:{</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;lib:&#39;../lib&#39;</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;},</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;shim:{</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&#39;a&#39;:[&#39;lib/jquery1.7&#39;]</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;},</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;map:{</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;a:{</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&#39;jquery&#39;:&#39;lib/jquery1.7.js&#39;</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;},</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;b:{</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&#39;jquery&#39;:&#39;lib/jquery1.9.js&#39;</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;},</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;config:{</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;c:{</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;size:&#39;large&#39;</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;},</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;d:{</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;color:&#39;red&#39;</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}</span><br />\r\n<span style="background-color:#FFFFE0;">&nbsp;&nbsp; &nbsp;});</span></p>\r\n\r\n<p style="line-height:12px;">&nbsp;</p>\r\n', '2016-11-22 07:23:54', '2016-12-06 17:57:46', 22, 0, ''),
(176, '', 0, 'AMD模块规范', NULL, '', '<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp; &nbsp;第一种：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;<span style="color:#008080;">define(function() {&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;return {&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;mix: function(source, target) {...}&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;};&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;});</span></p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;第二种：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; <span style="color:#808080;">&nbsp;//有依赖项</span><br />\r\n&nbsp;&nbsp; &nbsp;<span style="color:#008080;">define([&#39;data&#39;, &#39;ui&#39;], function(data, ui) {&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</span><span style="color:#808080;">// init here&nbsp;</span><br />\r\n<span style="color:#008080;">&nbsp;&nbsp; &nbsp;});</span></p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;第三种：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; <span style="color:#A9A9A9;">&nbsp;</span><span style="color:#808080;">//直接一个对象</span><br />\r\n&nbsp;&nbsp; &nbsp;<span style="color:#008080;">define({&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;data: [],&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;ui: []&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;});</span></p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;第四种：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;<span style="color:#808080;">//具名模块</span><br />\r\n&nbsp;&nbsp; &nbsp;<span style="color:#008080;">define(&#39;index&#39;, [&#39;data&#39;,&#39;base&#39;], function(data, base) {&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</span><span style="color:#808080;">// todo</span><span style="color:#008080;">&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;});</span></p>\r\n\r\n<p><br />\r\n&nbsp;&nbsp; &nbsp;第五种：<br />\r\n&nbsp;&nbsp; &nbsp;----------------------------------------------<br />\r\n&nbsp;&nbsp; &nbsp;<span style="color:#808080;">//包装模块</span><br />\r\n&nbsp;&nbsp; &nbsp;<span style="color:#008080;">define(function(require, exports, module) {&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;var base = require(&#39;base&#39;);&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;exports.show = function() {&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</span><span style="color:#808080;">// todo with module base&nbsp;</span><br />\r\n<span style="color:#008080;">&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;} &nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;});</span><br />\r\n<span style="color:#A9A9A9;">&nbsp;&nbsp; &nbsp;</span><span style="color:#808080;">//使用require获取模块，使用exports或者module.exports导出API</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>=============================================================================================</p>\r\n\r\n<ul>\r\n	<li>对于第四种写法 具名模块写法我们并不推荐的，因为不书写模块名我们一样可以调用，且在合并代码的时候，我们也可以根据代码自动生成模块名，如果我们现在写死了模块名，当某个时候，b.js我要移动到其他目录时候，JS也要跟着改，所以代码维护方面不好，所以不建议书写模块名。</li>\r\n	<li>对于第五种写法，requireJS中也是支持的，通过内部调用require来处理依赖模块</li>\r\n</ul>\r\n', '2016-12-07 05:49:10', '2016-12-06 17:49:06', 22, 0, '');
