(591, '', 0, 'moment.js', NULL, '', '<p style="line-height:14px;">中文文档：<a href="http://momentjs.cn/">http://momentjs.cn/</a></p>\r\n\r\n<p style="line-height:14px;">&nbsp;</p>\r\n\r\n<p style="line-height:14px;">yarn add moment</p>\r\n\r\n<p style="line-height:14px;">&nbsp;</p>\r\n\r\n<p style="line-height:14px;">var moment = require(&#39;moment&#39;);</p>\r\n\r\n<p style="line-height:14px;">&nbsp;</p>\r\n\r\n<p style="line-height:14px;">let time = X;</p>\r\n\r\n<p style="line-height:14px;"><span style="font-size:12px;">moment().subtract(1, &#39;day&#39;);<br />\r\nmoment().add(1, &#39;hour&#39;);</span></p>\r\n\r\n<p style="line-height:14px;"><span style="font-size:12px;">moment(X).format(&quot;</span>YYYY-MM-DD HH:mm:ss<span style="font-size:12px;">&quot;);</span></p>\r\n', '2019-08-14 11:23:40', '2019-10-01 10:04:39', 120, 0, ''),
(593, '', 0, '基础', NULL, '', '<p>中文文档v4：<a href="https://github.com/tianxuzhang/d3.v4-API-Translation" target="_blank">https://github.com/tianxuzhang/d3.v4-API-Translation</a><br />\r\n中文文档：<a href="https://d3js.org.cn/api/" target="_blank">https://d3js.org.cn/api/</a><br />\r\n原文文档：<a href="https://github.com/d3/d3/wiki" target="_blank">https://github.com/d3/d3/wiki</a><br />\r\n官方教程：<a href="https://github.com/d3/d3/wiki/Tutorials" target="_blank">https://github.com/d3/d3/wiki/Tutorials</a><br />\r\n一些demo：<a href="https://bl.ocks.org/mbostock" target="_blank">https://bl.ocks.org/mbostock</a></p>\r\n\r\n<p>demo: <a href="https://blog.csdn.net/xiang__liu/article/details/81017872" target="_blank">https://blog.csdn.net/xiang__liu/article/details/81017872</a></p>\r\n\r\n<p><a href="https://www.zhangxinxu.com/wordpress/2018/02/css-svg-canvas-text-pattern-overlay/" target="_blank">https://www.zhangxinxu.com/wordpress/2018/02/css-svg-canvas-text-pattern-overlay/</a>&nbsp; svg的混合模式&nbsp;</p>\r\n', '2019-08-16 08:26:59', '2019-08-16 08:26:48', 37, 0, ''),
(594, '', 0, 'vue.config.js', NULL, '', '<div>configureWebpack: {</div>\r\n\r\n<div style="margin-left: 40px;">plugins: [</div>\r\n\r\n<div style="margin-left: 80px;">new.webpack.ProvidePlugin({</div>\r\n\r\n<div style="margin-left: 120px;">Axios: &#39;axios&#39;</div>\r\n\r\n<div style="margin-left: 80px;">})</div>\r\n\r\n<div style="margin-left: 40px;">]</div>\r\n\r\n<div>}</div>\r\n\r\n<div>// 相当于在全局添加命名空间，即window.Axios，组件里调用时用Axios就好</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>\r\n<hr />\r\n<p>&nbsp;</p>\r\n</div>\r\n\r\n<div>transpileDependencies: [<br />\r\n&nbsp; &nbsp; &#39;vue-echarts&#39;<br />\r\n]<br />\r\n// 相当于Vue.use(vue-echarts组件)，组件含有install方法</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>\r\n<hr />\r\n<p>&nbsp;</p>\r\n\r\n<p>devServer: {<br />\r\n&nbsp; &nbsp; port: 8088,<br />\r\n&nbsp; &nbsp; host: &#39;0.0.0.0&#39;,<br />\r\n&nbsp; &nbsp; https: false,<br />\r\n&nbsp; &nbsp; open: true,<br />\r\n&nbsp; &nbsp; proxy: proxy<br />\r\n},<br />\r\ncss: {<br />\r\n&nbsp; &nbsp; loaderOptions: {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; sass: {<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; data: &#39;@import &quot;./src/theme/index.scss&quot;;&#39;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; }<br />\r\n&nbsp; &nbsp; }<br />\r\n},</p>\r\n</div>\r\n', '2019-08-16 08:53:36', '2019-08-17 03:03:11', 118, 0, ''),
(595, '', 0, '基础', NULL, '', '<p>W3school：http://www.w3school.com.cn/svg/svg_rect.asp<br />\r\nhttps://www.cnblogs.com/luoyong5213/p/7610505.html<br />\r\n滤镜：<br />\r\nhttps://www.cnblogs.com/laimeier/p/4146098.html<br />\r\nhttps://blog.csdn.net/onepunchmen00/article/details/78561561<br />\r\nhttps://blog.csdn.net/chy555chy/article/details/53364310<br />\r\n结论：svg不能做两圆的交集</p>\r\n', '2019-08-16 09:58:01', '2019-08-16 09:57:50', 122, 0, ''),
