(621, '', 9, 'API规范', NULL, '', '<div>增删改查 restful 规范：</div>\r\n\r\n<div>----------------------------------------------</div>\r\n\r\n<div>export const Xmodule = {</div>\r\n\r\n<div>// 列表</div>\r\n\r\n<div>&nbsp; &nbsp; list: (params) =&gt; {</div>\r\n\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp; return Axios.get(&#39;/mock-api/list&#39;, { params }).then(response =&gt; {</div>\r\n\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; return response.data.data;</div>\r\n\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp; });</div>\r\n\r\n<div>&nbsp; &nbsp; },</div>\r\n\r\n<div>&nbsp; &nbsp; // 新建</div>\r\n\r\n<div>&nbsp; &nbsp; add: (params) =&gt; {</div>\r\n\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp; return Axios.post(&#39;/mock-api/item&#39;, params).then(response =&gt; {</div>\r\n\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; return response.data.data;</div>\r\n\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp; });</div>\r\n\r\n<div>&nbsp; &nbsp; },</div>\r\n\r\n<div>&nbsp; &nbsp; // 修改</div>\r\n\r\n<div>&nbsp; &nbsp; mod: (params) =&gt; {</div>\r\n\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp; return Axios.put(&#39;/mock-api/item&#39;, params).then(response =&gt; {</div>\r\n\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; return response.data.data;</div>\r\n\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp; });</div>\r\n\r\n<div>&nbsp; &nbsp; },</div>\r\n\r\n<div>&nbsp; &nbsp; // 删除</div>\r\n\r\n<div>&nbsp; &nbsp; del: (params) =&gt; {</div>\r\n\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp; return Axios.delete(&#39;/mock-api/item&#39;, { params }).then(response =&gt; {</div>\r\n\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; return response.data.data;</div>\r\n\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp; });</div>\r\n\r\n<div>&nbsp; &nbsp; }</div>\r\n\r\n<div>};</div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div>加头部：</div>\r\n\r\n<div>----------------------------------------------</div>\r\n\r\n<div>\r\n<div>xxx: (params) =&gt; {</div>\r\n\r\n<div style="margin-left: 40px;">let headers = {<br />\r\n&nbsp; &nbsp; &#39;Content-Type&#39;: &#39;application/x-www-form-urlencoded; charset=UTF-8&#39;<br />\r\n};<br />\r\nreturn Axios.post(&#39;/mock-api/item&#39;, params, { headers }).then(response =&gt; {<br />\r\n&nbsp; &nbsp; return response.data.data;<br />\r\n});</div>\r\n\r\n<div>}</div>\r\n\r\n<div>&nbsp;</div>\r\n</div>\r\n\r\n<div>提交new FormData()数据用的头部：<br />\r\nlet headers = { &#39;Content-Type&#39;: &#39;multipart/form-data&#39; };</div>\r\n', '2019-09-17 09:44:36', '2019-09-17 09:44:39', 19, 0, ''),
